import React, { Component } from 'react';
import PropTypes from 'prop-types';
import {
  Button, Icon, Divider, Form,
} from 'semantic-ui-react';
import axios from 'axios';
import ImageUploader from '../utils/ImageUploader';
import config from '../../config.json';
import nameParser from '../../utils/nameParser';

class TeamGestion extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showModal: false,
      faction: '',
      name: '',
      money: 0,
      score: 0,
    };
  }

    handleSubmit = (data) => {
      if ('picture' in data.object) {
        this.props.updatePic(data.object.picture);
      }
      this.setState({ showModal: false });
    }

    handleSubmitForm = () => {
      axios({
        method: 'put',
        url: `${config.back_Url}/teams/${this.props.team.id}`,
        headers: { Token: localStorage.getItem('killerToken') },
        data: {
          name: this.state.name,
          faction: this.state.faction,
          score: this.state.score,
          money: this.state.money,
        },
      }).then((data) => {
        if (data.status === 200) {
          const up = data.data;
          this.props.updateState(up.name, up.faction, up.money, up.score);
        }
      });
    }

    handleDelete = () => {
      axios({
        method: 'delete',
        url: `${config.back_Url}/teams/${this.props.team.id}`,
        headers: { Token: localStorage.getItem('killerToken') },
      }).then((data) => {
        if (data.data.status === 200) {
          localStorage.setItem('teamId', null);
          window.location.assign('/');
        } else {
          // erreur
        }
      });
    }

    componentDidMount = () => {
      this.setState({
        faction: this.props.team.faction,
        name: this.props.team.name,
        score: this.props.team.score,
        money: this.props.team.money,
      });
    }

    handleChange = (event) => {
      const { target } = event;
      const { value } = target;
      const { name } = target;

      this.setState({
        [name]: value,
      });
    }

    handleSubmitChangeTeam = () => {
      const {
        name, leader, faction, picture, money, score,
      } = this.state;

      axios({
        method: 'put',
        url: `${config.back_Url}/teams/${this.props.team.id}`,
        headers: { Token: localStorage.getItem('killerToken') },
        data: {
          name,
          leader,
          faction,
          picture,
          money,
          score,
        },
      }).then((data) => {
        if (data.status === 200) {
          this.makeFeedback(
            'positive',
            'ok',
          );
          const up = data.data;
          this.props.updateState(up);
        } else {
          this.makeFeedback(
            'negative',
            'erreur du cul',
          );
        }
      });
    }

      handleChangeRadio = (value) => {
        this.setState({ faction: value });
        this.makeFeedback('positive',value)
      }

      render() {
        const { team } = this.props;
        const {
          faction, score, name, money,
        } = this.state;
        return (
          <div className="TeamGestion">
            <Divider />
            <h2>Gestion</h2>
            <Button size="small" type="button" onClick={() => this.setState({ showModal: true })}>
              <Icon name="photo" />
              Uploader une photo
            </Button>
            <Button size="small" color="red" type="button" onClick={this.handleDelete}>
              <Icon name="trash" />
              Supprimer l&apos;équipe
            </Button>
            <ImageUploader uploadUrl={`/teams/${team.id}`} type="team" open={this.state.showModal} handleSubmit={this.handleSubmit} />
            {this.props.admin ? (
              <div>
                <Form onSubmit={this.handleSubmitForm} className="spacing">
                  <Form.Field>
                    <label>Nom</label>
                    <input value={name} name="name" onChange={this.handleChange} />
                  </Form.Field>
                  <Form.Field>
                    <label>Argent</label>
                    <input value={money} name="money" onChange={this.handleChange} />
                  </Form.Field>
                  <Form.Field>
                    <label>Score</label>
                    <input value={score} name="score" onChange={this.handleChange} />
                  </Form.Field>
                  <Form.Group inline>
                    <label>Faction</label>
                    <Form.Radio
                      label={nameParser('OUVRIER')}
                      value="OUVRIER"
                      checked={faction === 'OUVRIER'}
                      onClick={() => this.handleChangeRadio('OUVRIER')}
                    />
                    <Form.Radio
                      label={nameParser('NOBLE')}
                      value="NOBLE"
                      checked={faction === 'NOBLE'}
                      onClick={() => this.handleChangeRadio('NOBLE')}
                    />
                    <Form.Radio
                      label={nameParser('AMET')}
                      value="AMET"
                      checked={faction === 'AMET'}
                      onClick={() => this.handleChangeRadio('AMET')}
                    />
                  </Form.Group>
                  <Button type="submit" onClick={() => this.handleSubmitChangeTeam()} >Modifier l&apos;équipe</Button>
                </Form>
              </div>
            ) : null}
          </div>
        );
      }
}

TeamGestion.propTypes = {
  admin: PropTypes.bool.isRequired,
  team: PropTypes.shape({
    id: PropTypes.number,
    picture: PropTypes.string,
    name: PropTypes.string,
    faction: PropTypes.oneOf(['NOBLE', 'OUVRIER', 'AMET']),
    score: PropTypes.number,
    money: PropTypes.number,
  }).isRequired,
  updatePic: PropTypes.func.isRequired,
  updateState: PropTypes.func.isRequired,
};

TeamGestion.defaultProp = {
  admin: false,
};

export default TeamGestion;
