import React, { Component } from 'react';
import {
  Segment, Loader, Card, Icon, Input,
} from 'semantic-ui-react';
import axios from 'axios';
import underscore from 'underscore';

import CardTeam from '../../components/team/CardTeam';

import config from '../../config.json';

export default class teams extends Component {
  constructor(props) {
    super(props);
    this.state = {
      teams: [],
      loading: true,
      search: [],
      val: '',
      sort: 'name',
    };
  }

  componentDidMount() {
    this.fetchData(0);
  }

  fetchData = (offset) => {
    axios({
      method: 'get',
      url: `${config.back_Url}/teams`,
      headers: { Token: localStorage.getItem('killerToken') },
      params: {
        offset,
      },
    }).then((data) => {
      if (data.data.status === 200) {
        const value = this.state.val.toLowerCase();
        const search = [];
        const arr = [
          ...this.state.teams,
          ...data.data.object,
        ];

        arr.forEach((team) => {
          if (team.name.toLowerCase().indexOf(value) > -1) {
            search.push(team);
          }
        });
        if (data.data.object.length === 10) {
          this.setState({
            loading: true,
            teams: arr,
            search,
          });
          this.fetchData(offset + 10);
        } else {
          this.setState({
            teams: arr,
            search,
            loading: false,
          });
        }
      }
    });
  }

  handleChange = (event, data) => {
    const value = data.value.toLowerCase();
    const search = [];
    this.state.teams.forEach((team) => {
      if (team.name.toLowerCase().indexOf(value) > -1) {
        search.push(team);
      }
    });
    this.setState({
      search,
      val: data.value,
    });
  }

  handleChangeSort = (event) => {
    this.setState({
      sort: event.target.value,
    });
  }

  render() {
    if (this.state.teams == null) {
      return (
        <Segment className="Loading">
          <Loader active className="test">Loading</Loader>
        </Segment>
      );
    }

    const { sort, search } = this.state;

    let TeamsToPrint = [];

    switch (sort) {
      case 'name':
        TeamsToPrint = search.sort((a, b) => a.name.localeCompare(b.name));
        break;

      case 'score':
        TeamsToPrint = search.sort((a, b) => a.score < b.score);
        break;

      case 'amet':
        search.forEach((team) => {
          if (team.faction === 'AMET') {
            TeamsToPrint.push(team);
          }
        });
        TeamsToPrint = TeamsToPrint.sort((a, b) => a.name.localeCompare(b.name));
        break;

      case 'noble':
        search.forEach((team) => {
          if (team.faction === 'NOBLE') {
            TeamsToPrint.push(team);
          }
        });
        TeamsToPrint = TeamsToPrint.sort((a, b) => a.name.localeCompare(b.name));
        break;

      case 'ouvrier':
        search.forEach((team) => {
          if (team.faction === 'OUVRIER') {
            TeamsToPrint.push(team);
          }
        });
        TeamsToPrint = TeamsToPrint.sort((a, b) => a.name.localeCompare(b.name));
        break;

      default:
        TeamsToPrint = search.sort((a, b) => a.name < b.name);
        break;
    }

    return (
      <div className="bodyDesktop">
        <div className="spacer" />
        <Segment>
          <h2>Équipes</h2>
          <Input focus placeholder="Search..." onChange={this.handleChange} className="InputDesktop" size="large" />
          <select className="InputDesktop SelectDesktop ui selection dropdown" onChange={this.handleChangeSort}>
            <option selected value="name">Nom</option>
            <option value="score">Nombre de points</option>
            <option value="amet">Faction amete</option>
            <option value="noble">Faction noble</option>
            <option value="ouvrier">Faction ouvrier</option>
          </select>

          <Card.Group doubling centered itemsPerRow={6} className="centered">
            {underscore.map(TeamsToPrint, (team) => (
              <CardTeam
                team={team}
                key={team.id}
                admin
              />
            ))}
          </Card.Group>
          {this.state.loading
            ? (
              <div className="SmallLoading">
                <Icon size="large" loading name="redo" />
                <p>Loading...</p>
              </div>
            )
            : null}
        </Segment>
        <div className="spacer" />
      </div>
    );
  }
}
