import React, {Component} from 'react';
import PropTypes from 'prop-types';
import {
    Segment, Loader, Card, Image, Divider, List, Grid, Table, Portal, Message,
} from 'semantic-ui-react';
import axios from 'axios';
import underscore from 'underscore';
import CardTeam from '../../components/team/CardTeam';
import UserGestion from '../../components/user/UserGestion';
import nouser from '../../img/nouser.png';
import dead from '../../img/dead.png';
import afk from '../../img/afk.png';
import config from '../../config.json';
import Responsive from "semantic-ui-react/dist/commonjs/addons/Responsive";
import CardUser from "../../components/user/CardUser";

class User extends Component {
    constructor(props) {
        super(props);
        this.state = {
            profile: null,
            team: null,
            kill: 0,
            death: 0,
            score: 0,
            kills: [],
            deaths: [],
            targets: null,
            hunters: [],
            message: '',
            variant: 'negative',
            open: false,
            personnalTarget: null
        };
    }

    componentDidMount() {
        axios({
            method: 'get',
            url: `${config.back_Url}/users/${this.props.match.params.id}`,
            headers: {Token: localStorage.getItem('killerToken')},
        }).then((data) => {
            if (data.data.status === 200) {
                console.log(data.data.object)
                this.setState({profile: data.data.object});
                if (data.data.object.userSkills.map(skill => skill.name).includes("Persécution")) {
                    axios({
                        method: 'get',
                        url: `${config.back_Url}/targets/user/unique/${this.props.match.params.id}`,
                        headers: {Token: localStorage.getItem('killerToken')},
                    }).then((target) => {
                        this.setState({personnalTarget: target.data.object})
                    })
                }
                axios({
                    method: 'get',
                    url: `${config.back_Url}/kills/user/${this.props.match.params.id}`,
                    headers: {Token: localStorage.getItem('killerToken')},
                }).then((data2) => {
                    if (data2.data.status === 200) {
                        this.setState({
                            kills: data2.data.object.kills,
                            kill: data2.data.object.kills.length,
                            deaths: data2.data.object.deaths,
                            death: data2.data.object.deaths.length,
                            score: data2.data.object.score,
                        });
                        if (this.state.profile.teamId != null) {
                            axios({
                                method: 'get',
                                url: `${config.back_Url}/teams/${this.state.profile.teamId}`,
                                headers: {Token: localStorage.getItem('killerToken')},
                            }).then((data3) => {
                                if (data3.data.status === 200) {
                                    this.setState({team: data3.data.object});
                                    axios({
                                        method: 'get',
                                        url: `${config.back_Url}/targets/user/${this.props.match.params.id}`,
                                        headers: {Token: localStorage.getItem('killerToken')},
                                    }).then((data4) => {
                                        if (data4.data.status === 200) {
                                            this.setState({targets: data4.data.object});
                                            underscore.map(this.state.targets, (target) => {
                                                axios({
                                                    method: 'get',
                                                    url: `${config.back_Url}/teams/${target.teamId}`,
                                                    headers: {Token: localStorage.getItem('killerToken')},
                                                }).then((data5) => {
                                                    if (data5.data.status === 200) {
                                                        this.setState((ps) => ({
                                                            hunters: [...ps.hunters, data5.data.object],
                                                        }));
                                                    }
                                                });
                                            });
                                        }
                                    });
                                }
                            });
                        }
                    }
                });
            }
        });
    }

    updatePic = (url) => {
        const {profile} = this.state;
        profile.picture = url;
        this.setState({profile});
        this.makeFeedback(
            'positive',
            'Image mise a jour',
        );
    }

    updateState = (state) => {
        const {profile} = this.state;

        for (const [key, value] of Object.entries(state)) {
            profile[key] = value
        }

        this.setState({
            profile: profile
        });
    }

    handleDeleteTarget = () => {
        const {personnalTarget} = this.state;

        axios({
            method: 'delete',
            url: `${config.back_Url}/targets/${personnalTarget.id}`,
            headers: {Token: localStorage.getItem('killerToken')},
        }).then((data) => {
            if (data.data.status === 200) {
                this.setState({personnalTarget: null});
            }
        });
    }

    makeFeedback = (variant, message) => {
        this.setState({
            message,
            variant,
            open: true,
        });
        setTimeout(() => {
            this.setState({
                message: null,
                variant: null,
                open: false,
            });
        }, 5000);
    }

    render() {
        const {
            profile, message, variant, open,
        } = this.state;

        if (profile == null) {
            return (
                <Segment className="Loading">
                    <Loader active className="test">Loading</Loader>
                </Segment>
            );
        }

        return (
            <div className="bodyDesktop">
                <div className="spacer"/>
                <Segment>
                    <Portal open={open}>
                        <Message
                            style={{
                                left: '20px',
                                position: 'fixed',
                                bottom: '20px',
                                zIndex: 1000,
                            }}
                            negative={variant === 'negative'}
                            positive={variant === 'positive'}
                        >
                            <Message.Header>{variant === 'negative' ? 'Une erreur est survenue' : "Tout s'est déroulé comme prévu"}</Message.Header>
                            {message}
                        </Message>
                    </Portal>
                    <Responsive as="div" minWidth={700}>
                        <Grid divided columns={2}>
                            <Grid.Row>
                                <Grid.Column width={6}>
                                    <div style={{width: 240, height: 300, objectFit: "cover"}}>
                                        <Image src={profile.picture === null ? nouser : profile.picture}
                                               style={{position: 'absolute', width: '240px'}}/>
                                        <Image src={profile.alive ? null : dead} style={{position: 'absolute'}}/>
                                        <Image src={profile.afk ? afk : null} style={{position: 'absolute'}}/>
                                    </div>
                                </Grid.Column>
                                <Grid.Column width={10}>
                                    <h1>{profile.pseudo}</h1>
                                    <h2>
                                        {profile.firstName}
                                        {' '}
                                        {profile.lastName}
                                    </h2>
                                    <List size="big">
                                        <List.Item>
                                            <List.Icon name="home"/>
                                            <List.Content>{profile.room}</List.Content>
                                        </List.Item>
                                        <List.Item>
                                            <List.Icon name="trophy"/>
                                            <List.Content>{this.state.score}</List.Content>
                                        </List.Item>
                                        <List.Item>
                                            <List.Icon name="crosshairs"/>
                                            <List.Content>{this.state.kill}</List.Content>
                                        </List.Item>
                                        <List.Item>
                                            <List.Icon name="times"/>
                                            <List.Content>{this.state.death}</List.Content>
                                        </List.Item>
                                        <List.Item>
                                            <i className="certificate icon"/>
                                            <List.Content>{profile.experience}</List.Content>
                                        </List.Item>
                                        <List.Item>
                                            <i className="building icon"/>
                                            <List.Content>{profile.school}</List.Content>
                                        </List.Item>
                                    </List>
                                </Grid.Column>
                            </Grid.Row>
                        </Grid>
                    </Responsive>
                    <Responsive as="div" maxWidth={700}>
                        <Grid divided columns={2}>
                            <Grid.Row>
                                <Grid.Column width={16}>
                                    <h1>{profile.pseudo}</h1>
                                    <div style={{width: 240, height: 300, objectFit: "cover"}}>
                                        <Image src={profile.picture === null ? nouser : profile.picture}/>
                                        <Image src={profile.alive ? null : dead}/>
                                        <Image src={profile.afk ? afk : null}/>
                                    </div>
                                    <h2>
                                        {profile.firstName}
                                        {' '}
                                        {profile.lastName}
                                    </h2>
                                    <List size="big">
                                        <List.Item>
                                            <List.Icon name="home"/>
                                            <List.Content>{profile.room}</List.Content>
                                        </List.Item>
                                        <List.Item>
                                            <List.Icon name="trophy"/>
                                            <List.Content>{this.state.score}</List.Content>
                                        </List.Item>
                                        <List.Item>
                                            <List.Icon name="crosshairs"/>
                                            <List.Content>{this.state.kill}</List.Content>
                                        </List.Item>
                                        <List.Item>
                                            <List.Icon name="times"/>
                                            <List.Content>{this.state.death}</List.Content>
                                        </List.Item>
                                    </List>
                                </Grid.Column>
                            </Grid.Row>
                        </Grid>
                    </Responsive>
                    {this.state.team !== null
                        ? (
                            <div>
                                <Divider/>
                                <h2>Équipe</h2>
                                <Card.Group doubling itemsPerRow={6} centered className="centered">
                                    <CardTeam team={this.state.team} admin key={this.state.profile.teamId + "team"}/>
                                </Card.Group>
                            </div>
                        ) : null}
                    {this.state.personnalTarget !== null
                        ?
                        <div>
                            <Divider/>
                            <h2>Cible Personnelle</h2>
                            <Card.Group doubling itemsPerRow={6} centered className="centered">
                                <CardUser user={this.state.personnalTarget.user} admin handleDelete={this.handleDeleteTarget}/>
                            </Card.Group>
                        </div> : null}
                    {this.state.kills !== null
                        ? (
                            <div>
                                <Divider/>
                                <h2>Meurtres</h2>
                                <Table celled padded sortable>
                                    <Table.Header>
                                        <Table.Row>
                                            <Table.HeaderCell>Victime</Table.HeaderCell>
                                            <Table.HeaderCell>Description</Table.HeaderCell>
                                            <Table.HeaderCell>Date</Table.HeaderCell>
                                            <Table.HeaderCell>Heure</Table.HeaderCell>
                                        </Table.Row>
                                    </Table.Header>
                                    <Table.Body>
                                        {this.state.kills.map((kill) => {
                                            if (kill.deathUser === null) {
                                                return null;
                                            }
                                            return (
                                                <Table.Row>
                                                    <Table.Cell textAlign="center">
                                                        <h5><a
                                                            href={`/users/${kill.deathUser.id}`}>{kill.deathUser.pseudo}</a>
                                                        </h5>
                                                    </Table.Cell>
                                                    <Table.Cell>{kill.description}</Table.Cell>
                                                    <Table.Cell>{kill.jour}</Table.Cell>
                                                    <Table.Cell>{kill.heure}</Table.Cell>
                                                </Table.Row>
                                            );
                                        })}
                                    </Table.Body>
                                </Table>
                            </div>
                        ) : null}
                    {this.state.deaths !== null
                        ? (
                            <div>
                                <Divider/>
                                <h2>Décès</h2>
                                <Table celled padded sortable>
                                    <Table.Header>
                                        <Table.Row>
                                            <Table.HeaderCell>Meurtrier</Table.HeaderCell>
                                            <Table.HeaderCell>Description</Table.HeaderCell>
                                            <Table.HeaderCell>Date</Table.HeaderCell>
                                            <Table.HeaderCell>Heure</Table.HeaderCell>
                                        </Table.Row>
                                    </Table.Header>
                                    <Table.Body>
                                        {this.state.deaths.map((kill) => (
                                            <Table.Row>
                                                <Table.Cell textAlign="center">
                                                    <h5><a
                                                        href={`/users/${kill.killerUser.id}`}>{kill.killerUser.pseudo}</a>
                                                    </h5>
                                                </Table.Cell>
                                                <Table.Cell>{kill.description}</Table.Cell>
                                                <Table.Cell>{kill.jour}</Table.Cell>
                                                <Table.Cell>{kill.heure}</Table.Cell>
                                            </Table.Row>
                                        ))}
                                    </Table.Body>
                                </Table>
                            </div>
                        ) : null}
                    <Divider/>
                    <UserGestion
                        profile={profile}
                        updateState={this.updateState}
                        updatePic={this.updatePic}
                    />
                    {this.state.hunters !== null
                        ? (
                            <div>
                                <Divider/>
                                <h2>Chasseurs</h2>
                                <Card.Group
                                    doubling
                                    itemsPerRow={6}
                                    centered
                                    className="centered"
                                >
                                    {underscore.map(this.state.hunters, (hunter, key) => (
                                        <CardTeam team={hunter} key={key + "hunter"}/>))}
                                </Card.Group>
                            </div>
                        ) : null}
                </Segment>
                <div className="spacer"/>
            </div>
        );
    }
}

User.propTypes = {
    match: PropTypes.shape({
        params: PropTypes.shape({
            id: PropTypes.string,
        }).isRequired,

    }).isRequired,
};

export default User;
