import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Segment, Form, Message } from 'semantic-ui-react';
import axios from 'axios';
import config from '../config.json';

class CreateTeam extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      faction: null,
      valide_name: true,
      valide_faction: true,
    };
  }

    handleChangeRadio = (e, { value }) => this.setState({ faction: value })

    handleSubmit() {
      if (this.state.faction === null) {
        if (this.state.name === '') {
          this.setState({
            valide_faction: false,
            valide_name: false,
          });
        } else {
          this.setState({
            valide_faction: false,
            valide_name: true,
          });
        }
      } else if (this.state.name === '') {
        this.setState({
          valide_faction: true,
          valide_name: false,
        });
      } else {
        axios({
          method: 'post',
          url: `${config.back_Url}/teams/`,
          headers: { Token: localStorage.getItem('killerToken') },
          data: {
            name: this.state.name,
            faction: this.state.faction,
          },
        }).then((data) => {
          if (data.data.status === 201) {
            localStorage.setItem('teamId', data.data.object.id);
            this.props.history.push(`/teams/${data.data.object.id}`);
          } else if (data.data.object !== null) {
            this.props.history.push(`/teams/${localStorage.getItem('teamId')}`);
          }
        });
      }
    }

    handleChange(event) {
      const { target } = event;
      const { value } = target;
      const { name } = target;

      this.setState({
        [name]: value,
      });
    }

    render() {
      const { faction } = this.state;
      return (
        <div className="bodyDesktop">
          <div className="spacer" />
          <Segment>
            <h2>Création d&apos;une équipe</h2>
            <Message warning hidden={this.state.valide_name}>
              Il faut rentrer un nom valide
            </Message>
            <Message warning hidden={this.state.valide_faction}>
              <Message.Content>Il faut choisir une faction</Message.Content>
            </Message>
            <Form onSubmit={() => this.handleSubmit()}>
              <Form.Group widths="equal" className="test">
                <Form.Input fluid name="name" label="name" placeholder="Nom" onChange={(e) => this.handleChange(e)} />
              </Form.Group>
              <Form.Group inline>
                <label>Faction</label>
                <Form.Radio
                  label="Ouvruer"
                  value="OUVRIER"
                  checked={faction === 'OUVRIER'}
                  onChange={this.handleChangeRadio}
                />
                <Form.Radio
                  label="Noble"
                  value="NOBLE"
                  checked={faction === 'NOBLE'}
                  onChange={this.handleChangeRadio}
                />
                <Form.Radio
                  label="Amet"
                  value="AMET"
                  checked={faction === 'AMET'}
                  onChange={this.handleChangeRadio}
                />
              </Form.Group>
              <Form.Button>Submit</Form.Button>
            </Form>
          </Segment>
          <div className="spacer" />
        </div>
      );
    }
}

CreateTeam.propTypes = {
  history: PropTypes.shape({
    push: PropTypes.func,
  }).isRequired,
};

export default CreateTeam;
