import React, { Component } from 'react';
import {
  Segment, Table,
} from 'semantic-ui-react';
import axios from 'axios';
import Histo from '../../components/Chart/histo';
import config from '../../config.json';
import nameParser from '../../utils/nameParser';

export default class Users extends Component {
  constructor(props) {
    super(props);
    this.state = {
      nbUsers: 0,
      nbAfks: 0,
      nbUserTeam: {
        global: 0,
        NOBLE: 0,
        AMET: 0,
        OUVRIER: 0,
      },
      nbAfkTeam: {
        global: 0,
        NOBLE: 0,
        AMET: 0,
        OUVRIER: 0,
      },
      nbTeam: {
        global: 0,
        NOBLE: 0,
        AMET: 0,
        OUVRIER: 0,
      },
      meanScoreUser: {
        global: 0,
        NOBLE: 0,
        AMET: 0,
        OUVRIER: 0,
      },
      meanKillUser: {
        global: 0,
        NOBLE: 0,
        AMET: 0,
        OUVRIER: 0,
      },
      meanDeathUser: {
        global: 0,
        NOBLE: 0,
        AMET: 0,
        OUVRIER: 0,
      },
      meanScoreTeam: {
        global: 0,
        NOBLE: 0,
        AMET: 0,
        OUVRIER: 0,
      },
      meanKillTeam: {
        global: 0,
        NOBLE: 0,
        AMET: 0,
        OUVRIER: 0,
      },
      meanDeathTeam: {
        global: 0,
        NOBLE: 0,
        AMET: 0,
        OUVRIER: 0,
      },
      enigma: [],
      kills: [],
    };

    this.fetchData = this.fetchData.bind(this);
  }

  componentDidMount() {
    this.fetchData(0);
  }

  fetchData(offset) {
    axios({
      method: 'get',
      url: `${config.back_Url}/stats/users`,
      headers: { Token: localStorage.getItem('killerToken') },
      params: {
        offset,
      },
    }).then((data) => {
      if (data.data.status === 200) {
        this.setState(data.data.object);
      }
    });

    axios({
      method: 'get',
      url: `${config.back_Url}/stats/teams`,
      headers: { Token: localStorage.getItem('killerToken') },
      params: {
        offset,
      },
    }).then((data) => {
      if (data.data.status === 200) {
        this.setState(data.data.object);
      }
    });

    axios({
      method: 'get',
      url: `${config.back_Url}/stats/enigma`,
      headers: { Token: localStorage.getItem('killerToken') },
      params: {
        offset,
      },
    }).then((data) => {
      if (data.data.status === 200) {
        this.setState({
          enigma: data.data.object,
        });
      }
    });

    const kills = [];
    const jour = ['LUNDI', 'MARDI', 'MERCREDI', 'JEUDI', 'VENDREDI'];
    const heure = ['08H00', '09H00', '10H00', '11H00', '12H00', '13H00', '14H00', '15H00', '16H00', '17H00', '18H00', '19H00', '20H00', '21H00', '22H00', '23H00'];

    heure.forEach((h) => {
      const dat = {
        name: h,

      };
      const da = [];
      jour.forEach((j) => {
        const d = {
          jour: j,
          data: 0,
        };
        da.push(d);
      });
      dat.data = da;
      kills.push(dat);
    });

    axios({
      method: 'get',
      url: `${config.back_Url}/kills`,
      headers: { Token: localStorage.getItem('killerToken') },
      params: {
        offset,
      },
    }).then((data) => {
      if (data.data.status === 200) {
        const obj = data.data.object;
        let i;
        for (i = 0; i < obj.length; i += 1) {
          const hour = parseInt(obj[i].heure.split(':')[0], 10) - 8;
          kills[hour].data[jour.indexOf(obj[i].jour)].data += 1;
        }
      }
      this.setState({
        kills,
      });
    });
  }

  render() {
    const {
      enigma,
    } = this.state;
    return (
      <div className="bodyDesktop">
        <div className="spacer" />
        <Segment>
          <h1>Statistiques</h1>
          <Table celled padded sortable>
            <Table.Header>
              <Table.Row>
                <Table.HeaderCell>Description</Table.HeaderCell>
                <Table.HeaderCell>
                  {`Faction ${nameParser('NOBLE')}`}
                </Table.HeaderCell>
                <Table.HeaderCell>
                  {`Faction ${nameParser('OUVRIER')}`}
                </Table.HeaderCell>
                <Table.HeaderCell>
                  {`Faction ${nameParser('AMET')}`}
                </Table.HeaderCell>
                <Table.HeaderCell>Total</Table.HeaderCell>
              </Table.Row>
            </Table.Header>
            <Table.Body>
              <Table.Row>
                <Table.Cell>
                  Nombre d&apos;inscrits
                </Table.Cell>
                <Table.Cell />
                <Table.Cell />
                <Table.Cell />
                <Table.Cell>
                  {this.state.nbUsers}
                </Table.Cell>
              </Table.Row>
              <Table.Row>
                <Table.Cell>
                  Nombre d&apos;AFK
                </Table.Cell>
                <Table.Cell />
                <Table.Cell />
                <Table.Cell />
                <Table.Cell>
                  {this.state.nbAfks}
                </Table.Cell>
              </Table.Row>
              <Table.Row>
                <Table.Cell>
                  Nombre de joueurs
                </Table.Cell>
                <Table.Cell>
                  {this.state.nbUserTeam.NOBLE}
                </Table.Cell>
                <Table.Cell>
                  {this.state.nbUserTeam.OUVRIER}
                </Table.Cell>
                <Table.Cell>
                  {this.state.nbUserTeam.AMET}
                </Table.Cell>
                <Table.Cell>
                  {this.state.nbUserTeam.global}
                </Table.Cell>
              </Table.Row>
              <Table.Row>
                <Table.Cell>
                  Nombre de joueurs afk
                </Table.Cell>
                <Table.Cell>
                  {this.state.nbAfkTeam.NOBLE}
                </Table.Cell>
                <Table.Cell>
                  {this.state.nbAfkTeam.OUVRIER}
                </Table.Cell>
                <Table.Cell>
                  {this.state.nbAfkTeam.AMET}
                </Table.Cell>
                <Table.Cell>
                  {this.state.nbAfkTeam.global}
                </Table.Cell>
              </Table.Row>
              <Table.Row>
                <Table.Cell>
                  Nombre d&apos;équipe
                </Table.Cell>
                <Table.Cell>
                  {this.state.nbTeam.NOBLE}
                </Table.Cell>
                <Table.Cell>
                  {this.state.nbTeam.OUVRIER}
                </Table.Cell>
                <Table.Cell>
                  {this.state.nbTeam.AMET}
                </Table.Cell>
                <Table.Cell>
                  {this.state.nbTeam.global}
                </Table.Cell>
              </Table.Row>
              <Table.Row>
                <Table.Cell>
                  Score moyen joueur
                </Table.Cell>
                <Table.Cell>
                  {this.state.meanScoreUser.NOBLE}
                </Table.Cell>
                <Table.Cell>
                  {this.state.meanScoreUser.OUVRIER}
                </Table.Cell>
                <Table.Cell>
                  {this.state.meanScoreUser.AMET}
                </Table.Cell>
                <Table.Cell>
                  {this.state.meanScoreUser.global}
                </Table.Cell>
              </Table.Row>
              <Table.Row>
                <Table.Cell>
                  Score moyen équipe
                </Table.Cell>
                <Table.Cell>
                  {this.state.meanScoreTeam.NOBLE}
                </Table.Cell>
                <Table.Cell>
                  {this.state.meanScoreTeam.OUVRIER}
                </Table.Cell>
                <Table.Cell>
                  {this.state.meanScoreTeam.AMET}
                </Table.Cell>
                <Table.Cell>
                  {this.state.meanScoreTeam.global}
                </Table.Cell>
              </Table.Row>
              <Table.Row>
                <Table.Cell>
                  Nombre de meurtre moyen joueur
                </Table.Cell>
                <Table.Cell>
                  {this.state.meanKillUser.NOBLE}
                </Table.Cell>
                <Table.Cell>
                  {this.state.meanKillUser.OUVRIER}
                </Table.Cell>
                <Table.Cell>
                  {this.state.meanKillUser.AMET}
                </Table.Cell>
                <Table.Cell>
                  {this.state.meanKillUser.global}
                </Table.Cell>
              </Table.Row>
              <Table.Row>
                <Table.Cell>
                  Nombre de meurtre moyen équipe
                </Table.Cell>
                <Table.Cell>
                  {this.state.meanKillTeam.NOBLE}
                </Table.Cell>
                <Table.Cell>
                  {this.state.meanKillTeam.OUVRIER}
                </Table.Cell>
                <Table.Cell>
                  {this.state.meanKillTeam.AMET}
                </Table.Cell>
                <Table.Cell>
                  {this.state.meanKillTeam.global}
                </Table.Cell>
              </Table.Row>
              <Table.Row>
                <Table.Cell>
                  Nombre de mort moyen joueur
                </Table.Cell>
                <Table.Cell>
                  {this.state.meanDeathUser.NOBLE}
                </Table.Cell>
                <Table.Cell>
                  {this.state.meanDeathUser.OUVRIER}
                </Table.Cell>
                <Table.Cell>
                  {this.state.meanDeathUser.AMET}
                </Table.Cell>
                <Table.Cell>
                  {this.state.meanDeathUser.global}
                </Table.Cell>
              </Table.Row>
              <Table.Row>
                <Table.Cell>
                  Nombre de mort moyen joueur
                </Table.Cell>
                <Table.Cell>
                  {this.state.meanDeathTeam.NOBLE}
                </Table.Cell>
                <Table.Cell>
                  {this.state.meanDeathTeam.OUVRIER}
                </Table.Cell>
                <Table.Cell>
                  {this.state.meanDeathTeam.AMET}
                </Table.Cell>
                <Table.Cell>
                  {this.state.meanDeathTeam.global}
                </Table.Cell>
              </Table.Row>
              {
                  enigma.map((e, key) => (
                    <Table.Row key={key}>
                      <Table.Cell>
                        {`Énigme "${e.name}"`}
                      </Table.Cell>
                      <Table.Cell>
                        {e.NOBLE}
                      </Table.Cell>
                      <Table.Cell>
                        {e.OUVRIER}
                      </Table.Cell>
                      <Table.Cell>
                        {e.AMET}
                      </Table.Cell>
                      <Table.Cell>
                        {e.global}
                      </Table.Cell>
                    </Table.Row>
                  ))
              }
            </Table.Body>
          </Table>
          <h2>Répartition des meurtres</h2>
          <div className="d3Container" style={{width:"100%"}}>
            <Histo data={this.state.kills} id="testHisto" />
          </div>
        </Segment>
        <div className="spacer" />
      </div>
    );
  }
}
