import React, { Component } from 'react';
import { Segment, Card, Icon } from 'semantic-ui-react';
import axios from 'axios';

import config from '../config.json';

export default class Users extends Component {
  constructor(props) {
    super(props);
    this.state = {
      enigmas: [],
    };
  }

  componentDidMount() {
    axios({
      method: 'get',
      url: `${config.back_Url}/enigmas/team/${localStorage.getItem('teamId')}`,
      headers: { Token: localStorage.getItem('killerToken') },
    }).then((data) => {
      if (data.data.status === 200) {
        this.setState({ enigmas: data.data.object });
      }
    });
  }

  render() {
    const { enigmas } = this.state;
    return (
      <div className="bodyDesktop">
        <div className="spacer" />
        <Segment>
          <h2>Énigmes</h2>
          <Card.Group doubling centered itemsPerRow={2} className="centered">
            {enigmas.map((enigma) => (
              <Card>
                <Card.Content header={enigma.name} />
                <Card.Content>
                  <div>{enigma.description}</div>
                  <div className="spacing">
                    {enigma.enigmaTeams[0].resolved
                      ? (
                        <div>
                          <Icon size="large" color="green" name="check" />
                          {' '}
                          Validée
                        </div>
                      )
                      : (
                        <div>
                          <Icon size="large" color="yellow" name="times" />
                          {' '}
                          En cours
                        </div>
                      )}
                  </div>
                </Card.Content>
                {enigma.hints.length > 0 ? (
                  <Card.Content>
                    <Card.Content header="Indices:" />
                    {enigma.hints.map((hint) => (<div>{hint.value}</div>))}
                  </Card.Content>
                ) : null}
              </Card>
            ))}
          </Card.Group>
        </Segment>
        <div className="spacer" />
      </div>
    );
  }
}
