import React, { Component } from 'react';
import {
  Segment, Table, Card, Icon, Image, Button, Modal, Form,
} from 'semantic-ui-react';
import axios from 'axios';

import config from '../../config.json';

export default class Users extends Component {
  constructor(props) {
    super(props);
    this.state = {
      kills: [],
      killer: null,
      killStreak: null,
      openModal: false,
      killEdit: null,
    };

    this.fetchData = this.fetchData.bind(this);
  }

  componentDidMount() {
    this.fetchData(0);
    this.fetchBestKiller();
    this.fetchKillStreak();
  }

  fetchKillStreak = () => {
    axios({
      method: 'get',
      url: `${config.back_Url}/kills/KillStreak`,
      headers: { Token: localStorage.getItem('killerToken') },
    }).then((data) => {
      if (data.data.status === 200) {
        this.setState({ killStreak: data.data.object });
      }
    });
  }

  fetchBestKiller = () => {
    axios({
      method: 'get',
      url: `${config.back_Url}/kills/bestkiller`,
      headers: { Token: localStorage.getItem('killerToken') },
    }).then((data) => {
      if (data.data.status === 200) {
        this.setState({ killer: data.data.object });
      }
    });
  }

  handleDelete = (id) => {
    axios({
      method: 'delete',
      url: `${config.back_Url}/kills/${id}`,
      headers: { Token: localStorage.getItem('killerToken') },
    }).then((data) => {
      if (data.data.status === 200) {
        const kills = [];
        this.state.kills.forEach((kill) => {
          if (kill.id !== id) {
            kills.push(kill);
          }
        });
        this.setState({ kills });
      }
    });
  }

  openModal = (kill) => {
    this.setState({
      openModal: true,
      killEdit: kill,
    });
  }

  closeModal = () => {
    this.setState({
      openModal: false,
      killEdit: null,
    });
  }

  handleChange = (event) => {
    const { target } = event;
    const { value } = target;
    const { name } = target;

    const { killEdit } = this.state;
    killEdit[name] = value;

    this.setState({
      killEdit,
    });
  }

  handleChangeRadio = (e, { value }) => {
    const { killEdit } = this.state;
    killEdit.jour = value;
    this.setState({ killEdit });
  }

  handleSubmit = () => {
    const { killEdit } = this.state;
    axios({
      method: 'put',
      url: `${config.back_Url}/kills/${killEdit.id}`,
      headers: { Token: localStorage.getItem('killerToken') },
      data: {
        description: killEdit.description,
        jour: killEdit.jour,
        heure: killEdit.heure,
        score: killEdit.score,
      },
    }).then((data) => {
      if (data.data.status === 200) {
        this.setState({ openModal: false, killEdit: null });
      }
    });
  }

  fetchData(offset) {
    axios({
      method: 'get',
      url: `${config.back_Url}/kills`,
      headers: { Token: localStorage.getItem('killerToken') },
      params: {
        offset,
      },
    }).then((data) => {
      if (data.data.status === 200) {
        const obj = data.data.object;
        if (obj.length === 10) {
          this.setState((pState) => ({
            kills: [
              ...pState.kills,
              ...obj,
            ],
          }));
          this.fetchData(offset + 10);
        } else {
          this.setState((pState) => ({
            kills: [
              ...pState.kills,
              ...obj,
            ],
          }));
        }
      }
    });
  }

  render() {
    const {
      kills, killStreak, killer, killEdit, openModal,
    } = this.state;
    return (
      <div className="bodyDesktop">
        <div className="spacer" />
        <Segment>
          <h2>Meurtres</h2>
          <Card.Group doubling centered itemsPerRow={6} className="centered">
            {killer !== null && killer.kills.length !== 0 ? (
              <Card>
                <Image wrapped src={killer.picture} />
                <Card.Content>
                  <Card.Header>{killer.pseudo}</Card.Header>
                  <Card.Meta>{`${killer.lastName} ${killer.firstName}`}</Card.Meta>
                  <Card.Description>
                    <Icon name="crosshairs" />
                    {killer.kills.length}
                  </Card.Description>
                </Card.Content>
                <Card.Content extra>
                  <Card.Description>Meilleur meurtrier</Card.Description>
                </Card.Content>
              </Card>
            ) : null}
            {killStreak !== null ? (
              <Card>
                <Image wrapped src={killStreak.user.picture} />
                <Card.Content>
                  <Card.Header>{killStreak.user.pseudo}</Card.Header>
                  <Card.Meta>{`${killStreak.user.lastName} ${killStreak.user.firstName}`}</Card.Meta>
                  <Card.Description>
                    <Icon name="crosshairs" />
                    {killStreak.kills}
                  </Card.Description>
                </Card.Content>
                <Card.Content extra>
                  <Card.Description>Meilleure s√©rie de victime</Card.Description>
                </Card.Content>
              </Card>
            ) : null}
          </Card.Group>
          <Table celled padded sortable>
            <Table.Header>
              <Table.Row>
                <Table.HeaderCell>Victime</Table.HeaderCell>
                <Table.HeaderCell>Meurtrier</Table.HeaderCell>
                <Table.HeaderCell>Points</Table.HeaderCell>
                <Table.HeaderCell>Description</Table.HeaderCell>
                <Table.HeaderCell>Date</Table.HeaderCell>
                <Table.HeaderCell>Heure</Table.HeaderCell>
                <Table.HeaderCell>Option</Table.HeaderCell>
              </Table.Row>
            </Table.Header>
            <Table.Body>
              {kills.map((kill) => (
                <Table.Row>
                  <Table.Cell textAlign="center">
                    <h5><a href={`/users/${kill.deathUser.id}`}>{kill.deathUser.pseudo}</a></h5>
                  </Table.Cell>
                  <Table.Cell textAlign="center">
                    <h5><a href={`/users/${kill.killerUser.id}`}>{kill.killerUser.pseudo}</a></h5>
                  </Table.Cell>
                  <Table.Cell>{kill.score}</Table.Cell>
                  <Table.Cell>{kill.description}</Table.Cell>
                  <Table.Cell>{kill.jour}</Table.Cell>
                  <Table.Cell>{kill.heure}</Table.Cell>
                  <Table.Cell>
                    <Button.Group>
                      <Button icon="edit" positive onClick={() => this.openModal(kill)} />

                      <Button icon="trash" negative onClick={() => this.handleDelete(kill.id)} />
                    </Button.Group>
                  </Table.Cell>
                </Table.Row>
              ))}
            </Table.Body>
          </Table>
          {killEdit !== null ? (
            <Modal
              open={openModal}
              onClose={() => this.closeModal()}
              closeOnEscape
              closeOnDimmerClick
              closeIcon
              style={{ width: '60vw', minHeight: '30vh', padding: '2em' }}
            >
              <Form>
                <Form.Group inline>
                  <label>Jour du meurtre</label>
                  <Form.Radio
                    label="Lundi"
                    value="LUNDI"
                    checked={killEdit.jour === 'LUNDI'}
                    onChange={this.handleChangeRadio}
                  />
                  <Form.Radio
                    label="Mardi"
                    value="MARDI"
                    checked={killEdit.jour === 'MARDI'}
                    onChange={this.handleChangeRadio}
                  />
                  <Form.Radio
                    label="Mercredi"
                    value="MERCREDI"
                    checked={killEdit.jour === 'MERCREDI'}
                    onChange={this.handleChangeRadio}
                  />
                  <Form.Radio
                    label="Jeudi"
                    value="JEUDI"
                    checked={killEdit.jour === 'JEUDI'}
                    onChange={this.handleChangeRadio}
                  />
                  <Form.Radio
                      label="Vendredi"
                      value="VENDREDI"
                      checked={killEdit.jour === 'VENDREDI'}
                      onChange={this.handleChangeRadio}
                  />
                </Form.Group>
                <Form.Group className="test">
                  <Form.Input type="time" name="time" label="Heure du meurtre" value={killEdit.heure} onChange={(e) => this.handleChange(e)} />
                </Form.Group>
                <Form.Group className="test">
                  <Form.Input type="number" name="score" label="Score" value={killEdit.score} onChange={(e) => this.handleChange(e)} />
                </Form.Group>
                <Form.Group widths="equal">
                  <Form.TextArea name="description" value={killEdit.description} label="Description du meurtre" placeholder="On veut du sang..." onChange={(e) => this.handleChange(e)} />
                </Form.Group>
                <Button positive onClick={this.handleSubmit}>Valider</Button>
                <Button negative onClick={this.closeModal}>Annuler</Button>
              </Form>
            </Modal>
          ) : null}
        </Segment>
        <div className="spacer" />
      </div>
    );
  }
}
