import React, { Component } from 'react';
import PropTypes from 'prop-types';
import {
  Form, Header, Button, Icon, Portal, Message,
} from 'semantic-ui-react';
import axios from 'axios';
import config from '../../config.json';
import ImageUploader from '../utils/ImageUploader';

class UserGestion extends Component {
  constructor(props) {
    super(props);
    this.state = {
      pseudo: '',
      room: '',
      admin: null,
      alive: false,
      afk: false,
      afkScore: 'null',
      roomError: false,
      showModal: false,
      teamId: null,
      message: '',
      variant: 'negative',
      open: false,
      phone: '',
    };
  }

    makeFeedback = (variant, message) => {
      this.setState({
        message,
        variant,
        open: true,
      });
      setTimeout(() => {
        this.setState({
          message: '',
          variant: 'positive',
          open: false,
        });
      }, 5000);
    }

    handleSubmitPicture = (data) => {
      if ('picture' in data) {
        this.props.updatePic(data.picture);
      }
      this.setState({ showModal: false });
    }

    handleSubmit = () => {
      const {
        room, pseudo, admin, alive, afk, afkScore, phone,
      } = this.state;
      if (/(^1([A-D]([1-4]0[1-9]|[2-4](1[0-9]|2[0-4]))|[ABD]11[0-7]|C11[0-5])$)|(^2([A-C][GD]|[DD])([0-3]|[GD])([12]|[GD])(0[0-6]|[1-4][12]|)$)|(^3(([GD][0-3]|C[1-3])(0[1-9]|10)|C00[1-8])$)|(^4([ABT]|C[A-E]|[D-H])[0-9]{3}$)|(^5A[0-9]{3}$)|(^6[A-Z][A-Z][0-9][0-9][0-9]$)/.test(room) === false) {
        this.setState({ roomError: true });
      }

      axios({
        method: 'put',
        url: `${config.back_Url}/users/${this.props.profile.id}`,
        headers: { Token: localStorage.getItem('killerToken') },
        data: {
          pseudo,
          room,
          admin,
          alive,
          afk,
          phone,
          afkScore,
        },
      }).then((data) => {
        if (data.status === 200) {
          this.makeFeedback(
            'positive',
            'ok',
          );
          const up = data.data;
          this.setState({
            roomError: false,
          });
          this.props.updateState(up);
        } else {
          this.makeFeedback(
            'negative',
            'erreur du cul',
          );
        }
      });
    }

    handleDelete = () => {
      axios({
        method: 'delete',
        url: `${config.back_Url}/users/${this.props.profile.id}`,
        headers: { Token: localStorage.getItem('killerToken') },
      }).then((data) => {
        if (data.data.status === 200) {
          window.location.assign('/');
        } else {
          this.makeFeedback(
            'negative',
            'erreur du cul',
          );
        }
      });
    }

    handleCheckbox = (prop) => {
      this.setState((prevState) => ({
        [prop]: !prevState[prop],
      }));
    }

    handleChange = (event) => {
      const { target } = event;
      const { value } = target;
      const { name } = target;
      this.setState({
        [name]: value,
      });
    }

    handleChangeDrop = (e, { name, value }) => {
      this.setState({ [name]: value });
    }

    componentDidMount = () => {
      this.setState({
        pseudo: this.props.profile.pseudo,
        room: this.props.profile.room,
        admin: this.props.profile.admin,
        alive: this.props.profile.alive,
        afk: this.props.profile.afk,
        afkScore: this.props.profile.afkScore,
        teamId: this.props.profile.teamId,
        phone: this.props.profile.phone,
      });
    }

    handleWeapon = (buy, primary) => {
      axios({
        method: 'put',
        url: `${config.back_Url}/users/${this.props.profile.id}/weapon/`,
        headers: { Token: localStorage.getItem('killerToken') },
        data: {
          buy,
          primary,
        },
      }).then((response) => {
        if (response.data.status === 200) {
          this.props.updateState(response.data.object);
          this.makeFeedback(
            'positive',
            "Tout s'est bien passé",
          );
        } else {
          this.makeFeedback(
            'negative',
            response.data.message,
          );
        }
      });
    }

    handleRezUser = () => {
      const { alive } = this.state;

      if (!alive) {
        axios({
          method: 'post',
          url: `${config.back_Url}/users/${this.props.profile.id}/rez`,
          headers: { Token: localStorage.getItem('killerToken') },
        }).then((response) => {
          if (response.data.status === 200) {
            this.props.updateState(response.data.object);
            this.makeFeedback(
              'positive',
              'ok',
            );
          } else {
            this.makeFeedback(
              'negative',
              'erreur du cul',
            );
          }
        });
      }
    }

    handleChangeRadio = (e, { value }) => this.setState({ admin: value })

    giveTime = () => {
      const message = ((new Date()).getUTCHours()).toString() + ' ' + ((new Date()).getSeconds()).toString();
      this.makeFeedback('positive',message)
    }

    render() {
      const {
        pseudo, room, alive, admin, afk, afkScore, roomError, teamId, message, variant, open, phone,
      } = this.state;
      return (
        <div>
          <Portal open={open}>
            <Message
              style={{
                left: '20px',
                position: 'fixed',
                bottom: '20px',
                zIndex: 1000,
              }}
              negative={variant === 'negative'}
              positive={variant === 'positive'}
            >
              <Message.Header>{variant === 'negative' ? 'Une erreur est survenue' : "Tout s'est déroulé comme prévu"}</Message.Header>
              {message}
            </Message>
          </Portal>
          <Header as="h2">Armes</Header>
          <Header as="h3">Arme principale</Header>
          <Button onClick={() => this.handleWeapon(true, true)} positive>
            Acheter une amélioration
          </Button>
          <Button onClick={() => this.handleWeapon(false, true)} negative>
            Revendre l&apos;amélioration
          </Button>
          <Button onClick={() => this.handleWeapon(true, true)} positive>
            Acheter une amélioration
          </Button>
          <Header as="h3">Arme secondaire</Header>
          <Button onClick={() => this.handleWeapon(false, false)} negative>
            Revendre l&apos;amélioration
          </Button>
          <Header as="h2">Gestion</Header>
          <Button type="button" onClick={() => this.setState({ showModal: true })}>
            <Icon name="photo" />
            Uploader une photo
          </Button>
          <Button onClick={this.handleDelete} negative>Supprimer le joueur</Button>
          <Button positive type="button" onClick={this.handleRezUser} disabled={alive || teamId === null}>
            <Icon name="ambulance" />
            Résurrectionner le joueur
          </Button>
          <Button onClick={() => this.giveTime()} positive>
            Donner la date
          </Button>
          <ImageUploader uploadUrl={`/users/${this.props.profile.id}`} type="user" open={this.state.showModal} handleSubmit={this.handleSubmitPicture} />
          <div className="spacing" />

          <Form>
            <Form.Input label="Pseudo" name="pseudo" value={pseudo} onChange={this.handleChange} />
            <Form.Input label="Room" name="room" value={room} onChange={this.handleChange} error={roomError ? { content: "Les règles s'appliquent aussi à toi petit con" } : false} />
            <Form.Input label="phone" name="phone" value={phone} onChange={this.handleChange} />
            <Form.Checkbox label="Alive" name="alive" checked={alive} onChange={() => this.handleCheckbox('alive')} />
            <label>status</label>
            <Form.Radio
              label="Joueur"
              value="JOUEUR"
              checked={admin === 'JOUEUR'}
              onChange={this.handleChangeRadio}
            />
            <Form.Radio
              label="Admin"
              value="ADMIN"
              checked={admin === 'ADMIN'}
              onChange={this.handleChangeRadio}
            />
            <Form.Radio
              label="Super Admin"
              value="SUPERADMIN"
              checked={admin === 'SUPERADMIN'}
              onChange={this.handleChangeRadio}
            />
            <Form.Checkbox label="Afk" name="afk" checked={afk} onChange={() => this.handleCheckbox('afk')} />
            <Form.Input label="Afk score" name="afkScore" type="number" value={afkScore} onChange={this.handleChange} />
            <Button type="submit" onClick={this.handleSubmit}>Valider</Button>
          </Form>
        </div>
      );
    }
}

UserGestion.propTypes = {
  updatePic: PropTypes.func.isRequired,
  profile: PropTypes.shape({
    id: PropTypes.number,
    pseudo: PropTypes.string,
    room: PropTypes.string,
    admin: PropTypes.oneOf(['JOUEUR', 'ADMIN', 'SUPERADMIN']),
    alive: PropTypes.bool,
    afk: PropTypes.bool,
