import React, {Component} from 'react';
import PropTypes from 'prop-types';
import {
    Segment, Loader, Divider, Card, Image, Grid, List, Icon, Button, Table,
} from 'semantic-ui-react';
import axios from 'axios';
import underscore from 'underscore';
import nameParser from '../../utils/nameParser';
import CardUser from '../../components/user/CardUser';
import TeamGestion from '../../components/team/TeamGestion';
import config from '../../config.json';
import SearchUser from '../../components/user/SearchUser';
import noTeamUrl from '../../img/noteam.png';

class Teams extends Component {
    constructor(props) {
        super(props);
        this.state = {
            team: null,
            users: [],
            open: false,
            targets: [],
            kills: [],
            enigmas: [],
        };
    }

    componentDidMount() {
        axios({
            method: 'get',
            url: `${config.back_Url}/teams/${this.props.match.params.id}`,
            headers: {Token: localStorage.getItem('killerToken')},
        }).then((data) => {
            if (data.data.status === 200) {
                this.setState({
                    team: data.data.object,
                });
                axios({
                    method: 'get',
                    url: `${config.back_Url}/teams/${this.props.match.params.id}/users/`,
                    headers: {Token: localStorage.getItem('killerToken')},
                }).then((data2) => {
                    if (data2.data.status === 200) {
                        this.setState({
                            users: data2.data.object,
                        });
                        axios({
                            method: 'get',
                            url: `${config.back_Url}/targets/team/${this.props.match.params.id}`,
                            headers: {Token: localStorage.getItem('killerToken')},
                        }).then((data3) => {
                            if (data3.data.status === 200) {
                                this.setState({
                                    targets: data3.data.object,
                                });
                                underscore.map(this.state.users, (user) => {
                                    axios({
                                        method: 'get',
                                        url: `${config.back_Url}/kills/user/${user.id}`,
                                        headers: {Token: localStorage.getItem('killerToken')},
                                    }).then((data4) => {
                                        if (data4.data.status === 200) {
                                            this.setState((ps) => ({
                                                kills: [...ps.kills, ...data4.data.object.kills],
                                            }));
                                        }
                                    });
                                });
                            }
                        });
                    }
                });
                axios({
                    method: 'get',
                    url: `${config.back_Url}/enigmas/admin/team/${this.props.match.params.id}`,
                    headers: {Token: localStorage.getItem('killerToken')},
                }).then((data5) => {
                    if (data5.data.status === 200) {
                        this.setState({enigmas: data5.data.object});
                    }
                });
            }
        });
    }


    updatePic = (url) => {
        const {team} = this.state;
        team.picture = url;
        this.setState({team});
    }

    updateState = (name, faction, money, score) => {
        const {team} = this.state;
        team.name = name;
        team.faction = faction;
        team.score = score;
        team.money = money;
        this.setState({
            team
        });
    }

    handleDeleteUser = (username) => {
        axios({
            method: 'delete',
            url: `${config.back_Url}/teams/${this.props.match.params.id}/user/`,
            headers: {Token: localStorage.getItem('killerToken')},
            data: {
                username,
            },
        }).then((data) => {
            if (data.data.status === 200) {
                if (data.data.object === true) {
                    const list = [];
                    this.state.users.map((user) => {
                        if (user.username !== username) {
                            list.push(user);
                        }
                        return null;
                    });
                    this.setState({users: list});
                }
            }
        });
    }

    handleAddUser = (data) => {
        if (data === null) {
            this.setState({
                open: false,
            });
            return;
        }
        axios({
            method: 'post',
            url: `${config.back_Url}/teams/${this.props.match.params.id}/user/`,
            headers: {Token: localStorage.getItem('killerToken')},
            data: {
                username: data.username,
            },
        }).then((data2) => {
            if (data2.data.status === 201) {
                if ('id' in data2.data.object) {
                    this.setState((ps) => ({
                        users: [
                            ...ps.users,
                            data2.data.object,
                        ],
                        open: false,
                    }));
                } else {
                    this.setState({
                        open: false,
                    });
                }
            }
        });
    }

    addTargets = () => {
        axios({
            method: 'post',
            url: `${config.back_Url}/targets/team/${this.props.match.params.id}`,
            headers: {Token: localStorage.getItem('killerToken')},
        }).then((data) => {
            if (data.data.status === 201) {
                axios({
                    method: 'get',
                    url: `${config.back_Url}/targets/team/${this.props.match.params.id}`,
                    headers: {Token: localStorage.getItem('killerToken')},
                }).then((dataTarget) => {
                    if (dataTarget.data.status === 200) {
                        this.setState({
                            targets: dataTarget.data.object,
                        });
                    }
                });
            } else {
                window.alert("C'est la merde. PrÃ©viens un admin");
            }
        });
    }

    handleDeleteTarget = (username) => {
        const {targets} = this.state;

        let id = 0;
        let i = 0;
        for (i = 0; i < targets.length; i += 1) {
            if (targets[i].user.username === username) {
                id = targets[i].id;
                break;
            }
        }
        if (id !== 0) {
            axios({
                method: 'delete',
                url: `${config.back_Url}/targets/${id}`,
                headers: {Token: localStorage.getItem('killerToken')},
            }).then((data) => {
                if (data.data.status === 200) {
                    targets.splice(i, 1);
                    this.setState({targets});
                }
            });
        }
    }

    deleteTargets = () => {
        axios({
            method: 'delete',
            url: `${config.back_Url}/targets/team/${this.props.match.params.id}`,
            headers: {Token: localStorage.getItem('killerToken')},
        }).then((data) => {
            if (data.data.status === 200) {
                this.setState({targets: []});
            }
        });
    }

    handleHint = (hint) => {
        axios({
            method: 'PUT',
            url: `${config.back_Url}/hints/hintTeam/${hint.hintTeams[0].id}`,
            headers: {Token: localStorage.getItem('killerToken')},
            data: {
                bought: !hint.hintTeams[0].bought,
            },
        }).then((response) => {
            if (response.data === null) {
                // do erro thingy thing
            } else if (response.data.status === 400) {
                // do other error thing
            } else {
                const {team} = this.state;
                const enigmas = [];
                this.state.enigmas.forEach((eni) => {
                    const enigma = eni;
                    enigma.hints = enigma.hints.map((hi) => {
                        const hin = hi;
                        if (hin.id === hint.id) {
                            hin.hintTeams[0].bought = !hin.hintTeams[0].bought;
                        }
                        return hin;
                    });

                    enigmas.push(enigma);
                });
                team.money += response.data.object;
                this.setState({
                    team,
                    enigmas,
                });
            }
        });
    }

    handleEnigma(enigma) {
        axios({
            method: 'put',
            url: `${config.back_Url}/enigmas/enigmaTeam/${enigma.enigmaTeams[0].id}`,
            headers: {Token: localStorage.getItem('killerToken')},
            data: {
                resolved: !enigma.enigmaTeams[0].resolved,
            },
        }).then((response) => {
            if (response.data === null) {
                // do erro thingy thing
            } else if (response.data.status === 400) {
                // do other error thing
            } else {
                const {team} = this.state;
                const enigmas = [];
                this.state.enigmas.forEach((eni) => {
                    const enig = eni;
                    if (enig.id === enigma.id) {
                        enig.enigmaTeams[0].resolved = !enig.enigmaTeams[0].resolved;
                    }
                    enigmas.push(enig);
                });
                team.money += response.data.object;
                this.setState({
                    team,
                    enigmas,
                });
            }
        });
    }

    render() {
        const {enigmas} = this.state;
        if (this.state.team === null) {
            return (
                <Segment className="Loading">
                    <Loader active className="test">Loading</Loader>
                </Segment>
            );
        }

        const {team, targets, users} = this.state;
        const admin = true;
        return (
            <div className="bodyDesktop">
                <div className="spacer"/>
                <Segment>
                    <div className="spacer"/>
                    <Grid divided columns={2}>
                        <Grid.Row>
                            <Grid.Column width={4}>
                                <Image size="medium" src={team.picture === null ? noTeamUrl : team.picture}/>
                            </Grid.Column>
                            <Grid.Column width={8}>
                                <h1>{team.name}</h1>
                                <List size="big">
                                    <List.Item>
                                        <List.Icon name="king chess"/>
                                        <List.Content>{team.leader}</List.Content>
                                    </List.Item>
                                    <List.Item>
                                        <List.Icon name="tag"/>
                                        <List.Content>{nameParser(team.faction)}</List.Content>
                                    </List.Item>
                                    <List.Item>
                                        <List.Icon name="trophy"/>
                                        <List.Content>{team.score}</List.Content>
                                    </List.Item>
                                    <List.Item>
                                        <List.Icon name="money"/>
                                        <List.Content>{team.money}</List.Content>
                                    </List.Item>
                                </List>
                            </Grid.Column>
                        </Grid.Row>
                    </Grid>
                    <Divider/>
                    <h2>Membres</h2>
                    <Card.Group doubling centered itemsPerRow={6} className="centered">
                        {underscore.map(users, (user) => (
                            <CardUser
                                user={user}
                                key={user.id}
                                admin={admin}
                                handleDelete={(username) => this.handleDeleteUser(username)}
                            />
                        ))}
                        {admin ?
                            <Card onClick={() => this.setState({open: true})}><Card.Content textAlign="center"><Icon
                                size="big" color="black" fitted name="add user"/></Card.Content></Card> : null}
                    </Card.Group>
                    <SearchUser
                        search="team"
                        open={this.state.open}
                        handleSubmit={this.handleAddUser}
                    />
                    {admin ? (
                        <TeamGestion
                            team={team}
                            updatePic={this.updatePic}
                            updateState={this.updateState}
                            admin
                        />
                    ) : null}

                    <Divider/>
                    <h2>Cibles</h2>
                    <Card.Group doubling centered itemsPerRow={6} className="centered">
                        {underscore.map(targets, (target) => {
                            const {user} = target;
                            return (
                                <CardUser
                                    user={user}
                                    key={user.id}
                                    admin={admin}
                                    handleDelete={(username) => this.handleDeleteTarget(username)}
                                />
                            );
                        })}
                    </Card.Group>
                    <div className="spacing"/>
                    <Button
                        onClick={this.addTargets}
                        disabled={targets.length >= 5}
                    >
                        Ajouter les cibles
                    </Button>
                    <Button
                        onClick={this.deleteTargets}
                        negative
                        disabled={targets.length <= 0}
                    >
                        Retirer les cibles
                    </Button>
                    {this.state.kills !== []
                        ? (
                            <div>
                                <Divider/>
                                <h2>Meurtres</h2>
                                <Table celled padded sortable>
                                    <Table.Header>
                                        <Table.Row>
                                            <Table.HeaderCell>Victime</Table.HeaderCell>
                                            <Table.HeaderCell>Description</Table.HeaderCell>
                                            <Table.HeaderCell>Date</Table.HeaderCell>
                                            <Table.HeaderCell>Heure</Table.HeaderCell>
                                        </Table.Row>
                                    </Table.Header>
                                    <Table.Body>
                                        {this.state.kills.map((kill) => (
                                            <Table.Row>
                                                <Table.Cell textAlign="center">
                                                    <h5><a
                                                        href={`/users/${kill.deathUser.id}`}>{kill.deathUser.pseudo}</a>
                                                    </h5>
                                                </Table.Cell>
                                                <Table.Cell>{kill.description}</Table.Cell>
                                                <Table.Cell>{kill.jour}</Table.Cell>
                                                <Table.Cell>{kill.heure}</Table.Cell>
                                            </Table.Row>
                                        ))}
                                    </Table.Body>
                                </Table>
                            </div>
                        ) : null}

                    <Divider/>
                    <h2>Ãnigme</h2>
                    <Card.Group doubling centered itemsPerRow={2} className="centered">
                        {enigmas.map((enigma) => (
                            <Card>
                                <Card.Content header={enigma.name}/>
                                <Card.Content>
                                    <div>{enigma.description}</div>
                                    <div className="spacing">
                                        {enigma.enigmaTeams[0].resolved
                                            ? <div><Button color="red"
                                                           onClick={() => this.handleEnigma(enigma)}>Refuser</Button>
                                            </div>
                                            : <div><Button color="green"
                                                           onClick={() => this.handleEnigma(enigma)}>Accepter</Button>
                                            </div>}
                                    </div>
                                </Card.Content>
                                {enigma.hints.length > 0 ? (
                                    <Card.Content>
                                        <Card.Content header="Indices"/>
                                        {enigma.hints.map((hint) => (
                                            <div>
                                                <Button onClick={() => this.handleHint(hint)}
                                                        color={hint.hintTeams[0].bought ? 'red' : 'green'}>{hint.price}</Button>
                                                {hint.value}
                                            </div>
                                        ))}
                                    </Card.Content>
                                ) : null}
                            </Card>
                        ))}
                    </Card.Group>

                </Segment>
                <div className="spacer"/>
            </div>
        );
    }
}

Teams.propTypes = {
    match: PropTypes.shape({
        params: PropTypes.shape({
            id: PropTypes.number,
        }).isRequired,

    }).isRequired,
};

export default Teams;
