import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Comment } from 'semantic-ui-react';
import noble from '../../img/noble.jpg';
import ouvrier from '../../img/ouvrier.jpg';
import amet from '../../img/amet.jpg';
import all from '../../img/logovictoria.png';

class Message extends Component {
  render() {
    const {
      id, titre, texte, faction,
    } = this.props;
    let img = all;
    switch (faction) {
      case 'NOBLE':
        img = noble;
        break;

      case 'AMET':
        img = amet;
        break;

      case 'OUVRIER':
        img = ouvrier;
        break;

      default:
        img = all;
        break;
    }
    if (this.props.admin === 'true') {
      return (
        <Comment>
          <Comment.Author style={{ marginTop: 10 }}>{titre}</Comment.Author>
          <Comment.Avatar src={img} style={{ marginRight: 20, marginTop: 10 }} />
          <Comment.Content>
            {texte}
          </Comment.Content>
          <Comment.Actions>
            <Comment.Action onClick={() => this.props.handleDelete(id)}>
              Supprimer
            </Comment.Action>
          </Comment.Actions>
        </Comment>
      );
    }

    return (
      <Comment>
        <Comment.Avatar src={img} style={{ marginRight: 20, marginTop: 10 }} />
        <Comment.Author style={{ marginTop: 10 }}>{titre}</Comment.Author>
        <Comment.Content>
          {texte}
        </Comment.Content>
      </Comment>
    );
  }
}

Message.propTypes = {
  id: PropTypes.number.isRequired,
  titre: PropTypes.string.isRequired,
  texte: PropTypes.string.isRequired,
  faction: PropTypes.oneOf(['NOBLE', 'OUVRIER', 'AMET']).isRequired,
  admin: PropTypes.bool.isRequired,
  handleDelete: PropTypes.func.isRequired,
};

export default Message;
