import React, {Component} from 'react';
import PropTypes from 'prop-types';
import {
    Segment, Loader, Divider, Card, Image, Grid, List, Icon, Table, Button,
} from 'semantic-ui-react';
import axios from 'axios';
import underscore from 'underscore';
import nameParser from '../utils/nameParser';
import CardUser from '../components/user/CardUser';
import TeamGestion from '../components/team/TeamGestion';
import config from '../config.json';
import SearchUser from '../components/user/SearchUser';
import noTeamUrl from '../img/noteam.png';
import Responsive from "semantic-ui-react/dist/commonjs/addons/Responsive";

class Team extends Component {
    constructor(props) {
        super(props);
        this.state = {
            team: null,
            users: [],
            open: false,
            targets: [],
            kills: [],
        };
    }

    componentDidMount() {
        axios({
            method: 'get',
            url: `${config.back_Url}/teams/${this.props.match.params.id}`,
            headers: {Token: localStorage.getItem('killerToken')},
        }).then((data) => {
            if (data.data.status === 200) {
                this.setState({
                    team: data.data.object,
                });
                axios({
                    method: 'get',
                    url: `${config.back_Url}/teams/${this.props.match.params.id}/users/`,
                    headers: {Token: localStorage.getItem('killerToken')},
                }).then((data2) => {
                    if (data2.data.status === 200) {
                        this.setState({
                            users: data2.data.object,
                        });
                        if (localStorage.getItem('teamId') === this.props.match.params.id) {
                            axios({
                                method: 'get',
                                url: `${config.back_Url}/targets/team/${this.props.match.params.id}`,
                                headers: {Token: localStorage.getItem('killerToken')},
                            }).then((data3) => {
                                if (data3.data.status === 200) {
                                    this.setState({
                                        targets: data3.data.object,
                                    });
                                    underscore.map(this.state.users, (user) => {
                                        axios({
                                            method: 'get',
                                            url: `${config.back_Url}/kills/user/${user.id}`,
                                            headers: {Token: localStorage.getItem('killerToken')},
                                        }).then((data4) => {
                                            if (data4.data.status === 200) {
                                                this.setState((ps) => ({
                                                    kills: [...ps.kills, ...data4.data.object.kills],
                                                }));
                                            }
                                        });
                                    });
                                }
                            });
                        }
                    }
                });
            }
        });
    }

    updatePic = (url) => {
        const {team} = this.state;
        team.picture = url;
        this.setState({team});
    }

    handleDeleteUser = (username) => {
        axios({
            method: 'delete',
            url: `${config.back_Url}/teams/${this.props.match.params.id}/user/`,
            headers: {Token: localStorage.getItem('killerToken')},
            data: {
                username,
            },
        }).then((data) => {
            if (data.data.status === 200) {
                if (data.data.object === true) {
                    const list = [];
                    this.state.users.forEach((user) => {
                        if (user.username !== username) {
                            list.push(user);
                        }
                    });
                    this.setState({users: list});
                }
            }
        });
    }

    handleAddUser = (data) => {
        if (data === null) {
            this.setState({
                open: false,
            });
            return;
        }
        axios({
            method: 'post',
            url: `${config.back_Url}/teams/${this.props.match.params.id}/user/`,
            headers: {Token: localStorage.getItem('killerToken')},
            data: {
                username: data.username,
            },
        }).then((data2) => {
            if (data2.data.status === 201) {
                if ('id' in data2.data.object) {
                    this.setState((ps) => ({
                        users: [
                            ...ps.users,
                            data2.data.object,
                        ],
                        open: false,
                    }));
                } else {
                    this.setState({
                        open: false,
                    });
                }
            } else {
                this.setState({
                    open: false,
                });
            }
        });
    }

    Users = () => {
        const {team} = this.state;
        const admin = team.leader === localStorage.getItem('username');
        return underscore.map(this.state.users, (user) => (
            <CardUser
                user={user}
                key={user.id}
                TeamLeader={admin}
                handleDelete={(username) => this.handleDeleteUser(username)}
            />
        ));
    }

    render() {
        if (this.state.team === null) {
            return (
                <Segment className="Loading">
                    <Loader active className="test">Loading</Loader>
                </Segment>
            );
        }

        const {team, targets} = this.state;
        const admin = team.leader === localStorage.getItem('username');
        return (
            <div className="bodyDesktop">
                <div className="spacer"/>
                <Segment>
                    <div className="spacer"/>
                    <Responsive as="div" minWidth={701}>
                        <Grid divided columns={2}>
                            <Grid.Row>
                                <Grid.Column width={6}>
                                    <Image size="medium" src={team.picture === null ? noTeamUrl : team.picture}/>
                                </Grid.Column>
                                <Grid.Column width={10}>
                                    <h1>{team.name}</h1>
                                    <List size="big">
                                        <List.Item>
                                            <List.Icon name="king chess"/>
                                            <List.Content>{team.leader}</List.Content>
                                        </List.Item>
                                        <List.Item>
                                            <List.Icon name="tag"/>
                                            <List.Content>{nameParser(team.faction)}</List.Content>
                                        </List.Item>
                                        <List.Item>
                                            <List.Icon name="trophy"/>
                                            <List.Content>{team.score}</List.Content>
                                        </List.Item>
                                        {team.money !== undefined ? (
                                            <List.Item>
                                                <List.Icon name="money"/>
                                                <List.Content>{team.money}</List.Content>
                                            </List.Item>
                                        ) : null}

                                    </List>
                                </Grid.Column>
                            </Grid.Row>
                        </Grid>
                    </Responsive>
                    <Responsive as="div" maxWidth={700}>
                        <Grid divided columns={2}>
                            <Grid.Row>
                                <Grid.Column width={16}>
                                    <h1>{team.name}</h1>
                                    <List size="big">
                                        <List.Item>
                                            <List.Icon name="king chess"/>
                                            <List.Content>{team.leader}</List.Content>
                                        </List.Item>
                                        <List.Item>
                                            <List.Icon name="tag"/>
                                            <List.Content>{team.faction}</List.Content>
                                        </List.Item>
                                        <List.Item>
                                            <List.Icon name="trophy"/>
                                            <List.Content>{team.score}</List.Content>
                                        </List.Item>
                                        {team.money !== undefined ? (
                                            <List.Item>
                                                <List.Icon name="money"/>
                                                <List.Content>{team.money}</List.Content>
                                            </List.Item>
                                        ) : null}

                                    </List>
                                </Grid.Column>
                            </Grid.Row>
                            <Grid.Row centered>
                                <Grid.Column width={14}>
                                    <Image size="large" src={team.picture === null ? noTeamUrl : team.picture}/>
                                </Grid.Column>
                            </Grid.Row>
                        </Grid>
                    </Responsive>
                    <Divider/>
                    <h2>Membres</h2>
                    <>
                        <Responsive as="div" minWidth={1001}>
                            <Card.Group centered itemsPerRow={6} className="centered">
                                {this.Users()}
                                {admin ? <Card onClick={() => this.setState({open: true})}><Card.Content
                                    textAlign="center"><Icon size="big" color="black" fitted
                                                             name="add user"/></Card.Content></Card> : null}
                                <SearchUser search="team" open={this.state.open} handleSubmit={this.handleAddUser}/>
                            </Card.Group>
                        </Responsive>
                        <Responsive as="div" minWidth={501} maxWidth={701}>
                            <Card.Group centered itemsPerRow={3} className="centered">
                                {this.Users()}
                                {admin ? <Card onClick={() => this.setState({open: true})}><Card.Content
                                    textAlign="center"><Icon size="big" color="black" fitted
                                                             name="add user"/></Card.Content></Card> : null}
                                <SearchUser search="team" open={this.state.open} handleSubmit={this.handleAddUser}/>
                            </Card.Group>
                        </Responsive>
                        <Responsive as="div" minWidth={701} maxWidth={1001}>
                            <Card.Group centered itemsPerRow={4} className="centered">
                                {this.Users()}
                                {admin ? <Card onClick={() => this.setState({open: true})}><Card.Content
                                    textAlign="center"><Icon size="big" color="black" fitted
                                                             name="add user"/></Card.Content></Card> : null}
                                <SearchUser search="team" open={this.state.open} handleSubmit={this.handleAddUser}/>
                            </Card.Group>
                        </Responsive>
                        <Responsive as="div" maxWidth={501}>
                            <Card.Group centered itemsPerRow={2} className="centered">
                                {this.Users()}
                                {admin ? <Card onClick={() => this.setState({open: true})}><Card.Content
                                    textAlign="center"><Icon size="big" color="black" fitted
                                                             name="add user"/></Card.Content></Card> : null}
                                <SearchUser search="team" open={this.state.open} handleSubmit={this.handleAddUser}/>
                            </Card.Group>
                        </Responsive>
                    </>

                    {targets.length > 0
                        ? (
                            <div>
                                <Divider/>
                                <h2>Cibles</h2>
                                <Card.Group doubling centered itemsPerRow={6} className="centered">
                                    {underscore.map(targets, (target) => {
                                        const {user} = target;
                                        return (<CardUser user={user} key={user.id}/>);
                                    })}
                                </Card.Group>
                            </div>
                        ) : null}
                    <Divider/>
                    <h2>Comptences</h2>
                    <Table celled padded sortable>
                        <Table.Header>
                            <Table.Row>
                                <Table.HeaderCell>Nom</Table.HeaderCell>
                                <Table.HeaderCell>Description</Table.HeaderCell>
                            </Table.Row>
                        </Table.Header>
                        <Table.Body>
                            {team.linkTeamSkills.map((skill, key) => (
                                <Table.Row key={key}>
                                    <Table.Cell textAlign="center">
                                        <h5>{skill.name}</h5>
                                    </Table.Cell>
                                    <Table.Cell>{skill.description}</Table.Cell>
                                </Table.Row>
                            ))}
                        </Table.Body>
                    </Table>

                    <Divider/>
                    <h2>Objets de l'quipe</h2>
                    <Table celled padded sortable>
                        <Table.Header>
                            <Table.Row>
                                <Table.HeaderCell>Nom</Table.HeaderCell>
                                <Table.HeaderCell>Description</Table.HeaderCell>
                                <Table.HeaderCell>Prix</Table.HeaderCell>
                            </Table.Row>
                        </Table.Header>
                        <Table.Body>
                            {teamItems.map((item, key) => (
                                <Table.Row key={key}>
                                    <Table.Cell textAlign="center">
                                        <h5>{item.name}</h5>
                                    </Table.Cell>
                                    <Table.Cell>{item.description}</Table.Cell>
                                    <Table.Cell>25</Table.Cell>
                                    

                                </Table.Row>
                            ))}
                        </Table.Body>
                    </Table>

                    {admin ? <TeamGestion team={team} updatePic={this.updatePic}/> : null}
                    {this.state.kills !== []
                        ? (
                            <div>
                                <Divider/>
                                <h2>Meurtres</h2>
                                <Table celled padded sortable>
                                    <Table.Header>
                                        <Table.Row>
                                            <Table.HeaderCell>Victime</Table.HeaderCell>
                                            <Table.HeaderCell>Description</Table.HeaderCell>
                                            <Table.HeaderCell>Date</Table.HeaderCell>
                                            <Table.HeaderCell>Heure</Table.HeaderCell>
                                        </Table.Row>
                                    </Table.Header>
                                    <Table.Body>
                                        {this.state.kills.map((kill) => (
                                            <Table.Row>
                                                <Table.Cell textAlign="center">
                                                    <h5><a
                                                        href={`/users/${kill.deathUser.id}`}>{kill.deathUser.pseudo}</a>
                                                    </h5>
                                                </Table.Cell>
                                                <Table.Cell>{kill.description}</Table.Cell>
                                                <Table.Cell>{kill.jour}</Table.Cell>
                                                <Table.Cell>{kill.heure}</Table.Cell>
                                            </Table.Row>
                                        ))}
                                    </Table.Body>
                                </Table>
                            </div>
                        ) : null}
                </Segment>
                <div className="spacer"/>
            </div>
        );
    }
}

Team.propTypes = {
    match: PropTypes.shape({
        params: PropTypes.shape({
            id: PropTypes.number,
        }).isRequired,

    }).isRequired,
};

export default Team;
