import React, { Component } from 'react';
import {
  Segment, Loader, Card, Icon, Input,
} from 'semantic-ui-react';
import axios from 'axios';
import underscore from 'underscore';

import CardUser from '../components/user/CardUser';

import config from '../config.json';
import Responsive from "semantic-ui-react/dist/commonjs/addons/Responsive";

export default class Users extends Component {
  constructor(props) {
    super(props);
    this.state = {
      users: [],
      loading: true,
      search: [],
      val: '',
    };

    this.handleChange = this.handleChange.bind(this);
  }

  componentDidMount() {
    this.fetchData(0);
  }

  fetchData =(offset) => {
    axios({
      method: 'get',
      url: `${config.back_Url}/users`,
      headers: { Token: localStorage.getItem('killerToken') },
      params: {
        offset,
      },
    }).then((data) => {
      if (data.data.status === 200) {
        const value = this.state.val.toLowerCase();
        const search = [];
        const arr = [
          ...this.state.users,
          ...data.data.object,
        ];
        arr.forEach((user) => {
          if (user.pseudo !== null) {
            if (user.pseudo !== null) {
              if (user.pseudo.toLowerCase().indexOf(value) > -1) {
                search.push(user);
                return;
              }
            }
            if ((`${user.lastName} ${user.firstName}`).toLowerCase().indexOf(value) > -1) {
              search.push(user);
            }
          }
        });

        if (data.data.object.length === 10) {
          this.setState({
            loading: true,
            users: arr,
            search,
          });
          this.fetchData(offset + 10);
        } else {
          this.setState({
            users: arr,
            search,
            loading: false,
          });
        }
      }
    });
  }

  handleChange = (event, data) => {
    const value = data.value.toLowerCase();
    const search = [];
    this.state.users.forEach((user) => {
      if (user.pseudo !== null) {
        if (user.pseudo !== null) {
          if (user.pseudo.toLowerCase().indexOf(value) > -1) {
            search.push(user);
            return;
          }
        }
        if ((`${user.lastName} ${user.firstName}`).toLowerCase().indexOf(value) > -1) {
          search.push(user);
        }
      }
    });
    this.setState({
      search,
      val: data.value,
    });
  }

  Users = () => underscore.map(this.state.search, (user) => (
    <CardUser user={user} key={user.id} />))

  render() {
    if (this.state.users == null) {
      return (
        <Segment className="Loading">
          <Loader active className="test">Loading</Loader>
        </Segment>
      );
    }

    return (
      <div className="bodyDesktop">
        <Segment>
          <h2>Joueurs</h2>
          <Input focus placeholder="Search..." onChange={this.handleChange} className="InputDesktop" size="large"/>
          <>
            <Responsive as="div" minWidth={1000}>
              <Card.Group centered itemsPerRow={6} className="centered">
                {this.Users()}
              </Card.Group>
            </Responsive>
            <Responsive as="div" minWidth={500} maxWidth={700}>
              <Card.Group centered itemsPerRow={3} className="centered">
                {this.Users()}
              </Card.Group>
            </Responsive>
            <Responsive as="div" minWidth={700} maxWidth={1000}>
              <Card.Group centered itemsPerRow={4} className="centered">
                {this.Users()}
              </Card.Group>
            </Responsive>
            <Responsive as="div" maxWidth={500}>
              <Card.Group centered itemsPerRow={2} className="centered">
                {this.Users()}
              </Card.Group>
            </Responsive>
          </>
          {this.state.loading
            ? (
              <div className="SmallLoading">
                <Icon size="large" loading name="redo" />
                <p>Loading...</p>
              </div>
            )
            : null}
        </Segment>
        <div className="spacer" />
      </div>
    );
  }
}
