import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Card, Image, Icon } from 'semantic-ui-react';

import nameParser from '../../utils/nameParser';

import noTeamUrl from '../../img/noteam.png';

class CardTeam extends Component {
  render() {
    const { team } = this.props;
    const uri = this.props.admin ? '/admin/teams/' : '/teams/';
    return (
      <Card href={uri + team.id}>
        <Image src={team.picture === null ? noTeamUrl : team.picture} wrapped ui={false} />
        <Card.Content>
          <Card.Header>{team.name}</Card.Header>
          <Card.Meta>{nameParser(team.faction)}</Card.Meta>
          <Card.Description>
            <Icon name="trophy" />
            {' '}
            {team.score}
          </Card.Description>
        </Card.Content>
      </Card>
    );
  }
}

CardTeam.propTypes = {
  admin: PropTypes.bool,
  team: PropTypes.shape({
    id: PropTypes.number,
    picture: PropTypes.string,
    name: PropTypes.string,
    faction: PropTypes.oneOf(['NOBLE', 'OUVRIER', 'AMET']),
    score: PropTypes.number,
  }).isRequired,
};

CardTeam.defaultProps = {
  admin: false,
};

export default CardTeam;
