import React, {Component} from 'react';
import {Card, Divider, Grid, Image, List, Loader, Segment, Table, Header, Button} from 'semantic-ui-react';
import axios from 'axios';
import CardTeam from '../components/team/CardTeam';
import nouser from '../img/nouser.png';
import dead from '../img/dead.png';
import afk from '../img/afk.png';
import SkillModal from '../components/skill/skillModal';

import config from '../config.json';
import Responsive from "semantic-ui-react/dist/commonjs/addons/Responsive";

class User extends Component {
    constructor(props) {
        super(props);
        this.state = {
            profile: null,
            team: null,
            kill: 0,
            death: 0,
            score: 0,
            kills: null,
            deaths: null,
            skills: [],
        };
    }

    componentDidMount() {
        axios({
            method: 'get',
            url: `${config.back_Url}/users/${this.props.match.params.id}`,
            headers: {Token: localStorage.getItem('killerToken')},
        }).then((data) => {
            if (data.data.status === 200) {
                this.setState({profile: data.data.object});
                this.getMySkills();
                axios({
                    method: 'get',
                    url: `${config.back_Url}/kills/user/${this.props.match.params.id}`,
                    headers: {Token: localStorage.getItem('killerToken')},
                }).then((data2) => {
                    if (data2.data.status === 200) {
                        this.setState({
                            kills: data2.data.object.kills,
                            deaths: data2.data.object.deaths,
                            kill: data2.data.object.kills.length,
                            death: data2.data.object.deaths.length,
                            score: data2.data.object.score,
                        });
                        if (this.state.profile.teamId != null) {
                            axios({
                                method: 'get',
                                url: `${config.back_Url}/teams/${this.state.profile.teamId}`,
                                headers: {Token: localStorage.getItem('killerToken')},
                            }).then((data3) => {
                                if (data3.data.status === 200) {
                                    this.setState({team: data3.data.object});
                                }
                            });
                        }
                    }
                });
            }
        });
    }

    makeFeedback = (variant, message) => {
        this.setState({
            message,
            variant,
            open: true,
        });
        setTimeout(() => {
            this.setState({
                message: '',
                variant: 'positive',
                open: false,
            });
        }, 5000);
    }

    // getWeapon = (weapon) => {
    //     switch (weapon) {
    //         case '5':
    //             return "Pas d'arme";

    //         case '4':
    //             return 'A5 A4';

    //         case '3':
    //             return 'A4 A3';

    //         case '2':
    //             return 'A3 A2';

    //         default:
    //             return null;
    //     }
    // }

    getMySkills = () => {
        console.log(this.props.profile)
        axios({
            method: 'get',
            url: `${config.back_Url}/users/${this.props.match.params.id}`,
            headers: {Token: localStorage.getItem('killerToken')},
            }).then((response) => {
                if (response.status == 200) {
                    this.setState({skills:response.data.object.userSkills});
                    (this.state.skills).map((skill)=>{skill.showDescription=false;})
                }
                else {
                    this.makeFeedback('negative','erreur de merde')
                }

            });
        
        }

    ViewDescription = (skill) => {
        
        skill.showDescription = true;
        this.makeFeedback('positive',skill.showDescription?'true':'false')
    }



    render() {
        if (this.state.profile == null) {
            return (
                <Segment className="Loading">
                    <Loader active className="test">Loading</Loader>
                </Segment>
            );
        }
        if (this.state.profile.pseudo == null) {
            return (<Segment textAlign="center" size="large">Cet utilisateur n&apos;existe pas !</Segment>);
        }
        const {profile,skills} = this.state;
        return (
            <div className="bodyDesktop">
                <div className="spacer"/>
                <Segment>
                    <Responsive as="div" minWidth={700}>
                        <Grid divided columns={2}>
                            <Grid.Row>
                                <Grid.Column width={6}>
                                    <div style={{width: 240, height: 300}}>
                                        <Image wrapped src={profile.picture === null ? nouser : profile.picture}/>
                                        <Image wrapped src={profile.alive ? null : dead}/>
                                        <Image wrapped src={profile.afk ? afk : null}/>
                                    </div>
                                </Grid.Column>
                                <Grid.Column width={10}>
                                    <h1>{profile.pseudo}</h1>
                                    <h2>
                                        {profile.firstName}
                                        {' '}
                                        {profile.lastName}
                                    </h2>
                                    <List size="big">
                                        <List.Item>
                                            <List.Icon name="home"/>
                                            <List.Content>{profile.room}</List.Content>
                                        </List.Item>
                                        <List.Item>
                                            <List.Icon name="trophy"/>
                                            <List.Content>{this.state.score}</List.Content>
                                        </List.Item>
                                        <List.Item>
                                            <List.Icon name="crosshairs"/>
                                            <List.Content>{this.state.kill}</List.Content>
                                        </List.Item>
                                        <List.Item>
                                            <List.Icon name="times"/>
                                            <List.Content>{this.state.death}</List.Content>
                                        </List.Item>
                                        <List.Item>
                                            <i className="certificate icon"/>
                                            <List.Content>{0}</List.Content>
                                        </List.Item>
                                        {/* <List.Item>
                                            <List.Icon name="hand paper outline"/>
                                            <List.Content>{this.getWeapon(profile.weaponPrimary)}</List.Content>
                                        </List.Item>
                                        <List.Item>
                                            <List.Icon flipped="horizontally" name="hand paper outline"/>
                                            <List.Content>{this.getWeapon(profile.weaponSecondary)}</List.Content>
                                        </List.Item> */}
                                    </List>
                                </Grid.Column>
                            </Grid.Row>
                        </Grid>
                    </Responsive>
                    <Responsive as="div" maxWidth={700}>
                        <Grid divided columns={2}>
                            <Grid.Row>
                                <Grid.Column width={16}>
                                    <h1>{profile.pseudo}</h1>
                                    <div style={{width: 240, height: 300, margin: 'auto'}}>
                                        <Image wrapped src={profile.picture === null ? nouser : profile.picture}/>
                                        <Image wrapped src={profile.alive ? null : dead}/>
                                        <Image wrapped src={profile.afk ? afk : null}/>
                                    </div>
                                    <h2>
                                        {profile.firstName}
                                        {' '}
                                        {profile.lastName}
                                    </h2>
                                    <List size="big">
                                        <List.Item>
                                            <List.Icon name="home"/>
                                            <List.Content>{profile.room}</List.Content>
                                        </List.Item>
                                        <List.Item>
                                            <List.Icon name="trophy"/>
                                            <List.Content>{this.state.score}</List.Content>
                                        </List.Item>
                                        <List.Item>
                                            <List.Icon name="crosshairs"/>
                                            <List.Content>{this.state.kill}</List.Content>
                                        </List.Item>
                                        <List.Item>
                                            <List.Icon name="times"/>
                                            <List.Content>{this.state.death}</List.Content>
                                        </List.Item>
                                        <List.Item>
                                            <i className="certificate icon"/>
                                            <List.Content>{0}</List.Content>
                                        </List.Item>
                                        <List.Item>
                                            <List.Icon name="hand paper outline"/>
                                            <List.Content>{this.getWeapon(profile.weaponPrimary)}</List.Content>
                                        </List.Item>
                                        <List.Item>
                                            <List.Icon flipped="horizontally" name="hand paper outline"/>
                                            <List.Content>{this.getWeapon(profile.weaponSecondary)}</List.Content>
                                        </List.Item>
                                    </List>
                                </Grid.Column>
                            </Grid.Row>
                        </Grid>
                    </Responsive>
                    {this.state.team !== null
                        ? (
                            <div>
                                <Divider/>
                                <h2>quipe</h2>
                                <Card.Group doubling itemsPerRow={6} centered className="centered">
                                    <CardTeam team={this.state.team} key={this.state.profile.teamId}/>
                                </Card.Group>
                            </div>
                        ) : null}
                    {this.state.kills !== null
                        ? (
                            <div>
                                <Divider/>
                                <h2>Meurtres</h2>
                                <Table celled padded sortable>
                                    <Table.Header>
                                        <Table.Row>
                                            <Table.HeaderCell>Victime</Table.HeaderCell>
                                            <Table.HeaderCell>Description</Table.HeaderCell>
                                            <Table.HeaderCell>Date</Table.HeaderCell>
                                            <Table.HeaderCell>Heure</Table.HeaderCell>
                                        </Table.Row>
                                    </Table.Header>
                                    <Table.Body>
                                        {this.state.kills.map((kill) => (
                                            <Table.Row>
                                                <Table.Cell textAlign="center">
                                                    <h5><a
                                                        href={`/users/${kill.deathUser.id}`}>{kill.deathUser.pseudo}</a>
                                                    </h5>
                                                </Table.Cell>
                                                <Table.Cell>{kill.description}</Table.Cell>
                                                <Table.Cell>{kill.jour}</Table.Cell>
                                                <Table.Cell>{kill.heure}</Table.Cell>
                                            </Table.Row>
                                        ))}
                                    </Table.Body>
                                </Table>
                            </div>
                        ) : null}
                    {this.state.deaths !== null
                        ? (
                            <div>
                                <Divider/>
                                <h2>Dcs</h2>
                                <Table celled padded sortable>
                                    <Table.Header>
                                        <Table.Row>
                                            <Table.HeaderCell>Meurtrier</Table.HeaderCell>
                                            <Table.HeaderCell>Description</Table.HeaderCell>
                                            <Table.HeaderCell>Date</Table.HeaderCell>
                                            <Table.HeaderCell>Heure</Table.HeaderCell>
                                        </Table.Row>
                                    </Table.Header>
                                    <Table.Body>
                                        {this.state.deaths.map((kill) => (
                                            <Table.Row>
                                                <Table.Cell textAlign="center">
                                                    <h5><a
                                                        href={`/users/${kill.killerUser.id}`}>{kill.killerUser.pseudo}</a>
                                                    </h5>
                                                </Table.Cell>
                                                <Table.Cell>{kill.description}</Table.Cell>
                                                <Table.Cell>{kill.jour}</Table.Cell>
                                                <Table.Cell>{kill.heure}</Table.Cell>
                                            </Table.Row>
                                        ))}
                                    </Table.Body>
                                </Table>
                            </div>
                        ) : null}
                </Segment>
                <Header as="h2">Mes Comptences</Header>
          <List size='big'>
            {skills.map((skill)=>{return( 
            <List.Item> 
                <List.Content >
                    {skill.name}
                    <Button onClick={()=>this.ViewDescription(skill)} style = {{marginLeft:'2em'}}>
                        Voir plus
                    </Button>
                    <SkillModal skill={skill}/>             
                </List.Content> 
                
            </List.Item>)})

              }
          </List>
          <br></br>
            </div>
        );
    }
}

export default User;
