import React, { Component } from 'react';
import PropTypes from 'prop-types';
import {
  Form, Header, Button, Icon, Portal, Message, List, 
} from 'semantic-ui-react';
import axios from 'axios';
import config from '../../config.json';
import ImageUploader from '../utils/ImageUploader';
import SkillModal from '../skill/skillModal.jsx';

class UserGestion extends Component {
    constructor(props) {
        super(props);
        this.state = {
            pseudo: '',
            room: '',
            admin: null,
            alive: false,
            afk: false,
            afkScore: 'null',
            roomError: false,
            showModal: false,
            teamId: null,
            message: '',
            variant: 'negative',
            open: false,
            phone: '',
            skills: [],
            otherSkills: [],
        };
    }

    makeFeedback = (variant, message) => {
        console.log('makeFeedback');
        this.setState({
            message,
            variant,
            open: true,
        });
        setTimeout(() => {
            this.setState({
                message: '',
                variant: 'positive',
                open: false,
            });
        }, 5000);
    }

    handleSubmitPicture = (data) => {
        console.log('handleSubmitPicture');
        if ('picture' in data.object) {
            this.props.updatePic(data.picture);
        }
        this.setState({showModal: false});
    }

    handleSubmit = () => {
        console.log('handleSubmit');
        const {
            room, pseudo, admin, alive, afk, afkScore, phone,
        } = this.state;
        if (/(^1([A-D]([1-4]0[1-9]|[2-4](1[0-9]|2[0-4]))|[ABD]11[0-7]|C11[0-5])$)|(^2([A-C][GD]|[DD])([0-3]|[GD])([12]|[GD])(0[0-6]|[1-4][12]|)$)|(^3(([GD][0-3]|C[1-3])(0[1-9]|10)|C00[1-8])$)|(^4([ABT]|C[A-E]|[D-H])[0-9]{3}$)|(^5A[0-9]{3}$)|(^6[A-Z][A-Z][0-9][0-9][0-9]$)/.test(room) === false) {
            this.setState({roomError: true});
        }

        axios({
            method: 'put',
            url: `${config.back_Url}/users/${this.props.profile.id}`,
            headers: {Token: localStorage.getItem('killerToken')},
            data: {
                pseudo,
                room,
                admin,
                alive,
                afk,
                phone,
                afkScore,
            },
        }).then((data) => {
            if (data.status === 200) {
                this.makeFeedback(
                    'positive',
                    'ok',
                );
                const up = data.data;
                this.setState({
                    roomError: false,
                });
                this.props.updateState(up);
            } else {
                this.makeFeedback(
                    'negative',
                    'erreur du cul',
                );
            }
        });

        axios({
            method: 'put',
            url: `${config.back_Url}/users/${this.props.profile.id}`,
            headers: {Token: localStorage.getItem('killerToken')},
            data: {
                pseudo,
                room,
                admin,
                alive,
                afk,
                phone,
                afkScore,
            },
        }).then((data) => {
            if (data.status === 200) {
                this.makeFeedback(
                    'positive',
                    'ok',
                );
                const up = data.data.object;
                this.setState({
                    roomError: false,
                });
                this.props.updateState(up);
            } else {
                this.makeFeedback(
                    'negative',
                    'erreur du cul',
                );
            }
        });
    }


    handleDelete = () => {
        console.log('handleDelete');
        axios({
            method: 'delete',
            url: `${config.back_Url}/users/${this.props.profile.id}`,
            headers: {Token: localStorage.getItem('killerToken')},
        }).then((data) => {
            if (data.data.status === 200) {
                window.location.assign('/');
            } else {
                this.makeFeedback(
                    'negative',
                    'erreur du cul',
                );
            }
        });
    }

    handleCheckbox = (prop) => {
        console.log('handleCheckbox');
        this.setState((prevState) => ({
            [prop]: !prevState[prop],
        }));
    }

    handleChange = (event) => {
        console.log('handleChange');
        const {target} = event;
        const {value} = target;
        const {name} = target;
        this.setState({
            [name]: value,
        });
    }

    handleChangeDrop = (e, {name, value}) => {
        console.log('handleChangeDrop');
        this.setState({[name]: value});
    }

    componentDidMount = () => {
        console.log('componentDidMount');
        this.setState({
            pseudo: this.props.profile.pseudo,
            room: this.props.profile.room,
            admin: this.props.profile.admin,
            alive: this.props.profile.alive,
            afk: this.props.profile.afk,
            afkScore: this.props.profile.afkScore,
            teamId: this.props.profile.teamId,
            phone: this.props.profile.phone,
        });
        
        this.getMySkills();
    }

    handleWeapon = (buy, primary) => {
        console.log('handleWeapon');
        axios({
            method: 'put',
            url: `${config.back_Url}/users/${this.props.profile.id}/weapon/`,
            headers: {Token: localStorage.getItem('killerToken')},
            data: {
                buy,
                primary,
            },
        }).then((response) => {
            if (response.data.status === 200) {
                this.props.updateState(response.data.object);
                this.makeFeedback(
                    'positive',
                    "Tout s'est bien passé",
                );
            } else {
                this.makeFeedback(
                    'negative',
                    response.data.message,
                );
            }
        });
    }

    handleRezUser = () => {
        console.log('handleRezUser');
        const {alive} = this.state;

        if (!alive) {
            axios({
                method: 'post',
                url: `${config.back_Url}/users/${this.props.profile.id}/rez`,
                headers: {Token: localStorage.getItem('killerToken')},
            }).then((response) => {
                if (response.data.status === 200) {
                    this.props.updateState(response.data.object);
                    this.makeFeedback(
                        'positive',
                        'ok',
                    );
                } else {
                    this.makeFeedback(
                        'negative',
                        'erreur du cul',
                    );
                }
            });
        }
    }

    handleChangeRadio = (e, {value}) => {console.log('handleChangeRadio');this.setState({admin: value})}

    giveTime = () => {
        console.log('giveTime');
        const message = ((new Date()).getUTCHours() + 2).toString() + ' ' + ((new Date()).getUTCMinutes()).toString() + ' ' + ((new Date()).getUTCSeconds()).toString();
        this.makeFeedback('positive', message)
    }

    getMySkills = () => {
        console.log('getMySkills');
        axios({
            method: 'get',
            url: `${config.back_Url}/users/${this.props.profile.id}`,
            headers: {Token: localStorage.getItem('killerToken')},
            }).then((response) => {
                if (response.status == 200) {
                    this.setState({skills:response.data.object.userSkills});
                    (this.state.skills).forEach((skill)=>{skill.showDescription=false;
                    skill.buyable=false;})
                }
                else {
                    this.makeFeedback('negative','erreur de merde')
                }

            });
        
