    render() {
      const {
        pseudo, room, alive, admin, afk, afkScore, roomError, teamId, message, variant, open, phone, skills, otherSkills
      } = this.state;
      return (
        <div>
          <Portal open={open}>
            <Message
              style={{
                left: '20px',
                position: 'fixed',
                bottom: '20px',
                zIndex: 1000,
              }}
              negative={variant === 'negative'}
              positive={variant === 'positive'}
            >
              <Message.Header>{variant === 'negative' ? 'Une erreur est survenue' : "Tout s'est déroulé comme prévu"}</Message.Header>
              {message}
            </Message>
          </Portal>
          <Header as="h2">Mes Compétences</Header>
          <List size='big'>
            {skills.map((skill)=>{return( 
            <List.Item> 
                <List.Content>
                    {skill.name}
                    <Button onClick={()=>this.ViewDescription(skill)}>
                        Voir plus
                    </Button>
                    <SkillModal skill={skill}/>             
                </List.Content> 
                
            </List.Item>)})

              }
          </List>
          {/* <Header as="h2">Acheter une Compétence</Header>
          <List size='big'>
        {otherSkills.map((skill)=>{return( 
        <List.Item> 
            <List.Content>
                {skill.name}
                <Button onClick={()=>this.ViewDescription(skill)}>
                    Voir plus
                </Button>
                <SkillModal open ={true} skill={skill}/>             
            </List.Content> 
            
        </List.Item>)})

