import React, { Component } from 'react';
import {
  Segment, Table, Card, Icon, Image,
} from 'semantic-ui-react';
import axios from 'axios';
import Responsive from "semantic-ui-react/dist/commonjs/addons/Responsive";

import config from '../config.json';

export default class Users extends Component {
  constructor(props) {
    super(props);
    this.state = {
      kills: [],
      killer: null,
      killStreak: null,
    };

    this.fetchData = this.fetchData.bind(this);
  }

  componentDidMount() {
    this.fetchData(0);
    this.fetchBestKiller();
    this.fetchKillStreak();
  }

  fetchData = (offset) => {
    axios({
      method: 'get',
      url: `${config.back_Url}/kills`,
      headers: { Token: localStorage.getItem('killerToken') },
      params: {
        offset,
      },
    }).then((data) => {
      if (data.data.status === 200) {
        if (data.data.object.length === 10) {
          this.setState((ps) => ({
            kills: [
              ...ps.kills,
              ...data.data.object,
            ],
          }));
          this.fetchData(offset + 10);
        } else {
          this.setState((ps) => ({
            kills: [
              ...ps.kills,
              ...data.data.object,
            ],
          }));
        }
      }
    });
  }

    fetchKillStreak = () => {
      axios({
        method: 'get',
        url: `${config.back_Url}/kills/KillStreak`,
        headers: { Token: localStorage.getItem('killerToken') },
      }).then((data) => {
        if (data.data.status === 200) {
          this.setState({ killStreak: data.data.object });
        }
      });
    }

    fetchBestKiller = () => {
      axios({
        method: 'get',
        url: `${config.back_Url}/kills/bestkiller`,
        headers: { Token: localStorage.getItem('killerToken') },
      }).then((data) => {
        if (data.data.status === 200) {
          this.setState({ killer: data.data.object });
        }
      });
    }

    Cards = () => {
      const { killer, killStreak } = this.state;
      return (
        <>
          {killer !== null
            ? (
              <Card>
                <Image src={killer.user.picture} wrapped />
                <Card.Content>
                  <Card.Header>{killer.user.pseudo}</Card.Header>
                  <Card.Meta>{`${killer.user.lastName} ${killer.user.firstName}`}</Card.Meta>
                  <Card.Description>
                    <Icon name="crosshairs" />
                    {killer.kills}
                  </Card.Description>
                </Card.Content>
                <Card.Content extra>
                  <Card.Description>Meilleur meurtrier</Card.Description>
                </Card.Content>
              </Card>
            ) : null}
          {killStreak !== null
            ? (
              <Card>
                <Image src={killStreak.user.picture} wrapped />
                <Card.Content>
                  <Card.Header>{killStreak.user.pseudo}</Card.Header>
                  <Card.Meta>{`${killStreak.user.lastName} ${killStreak.user.firstName}`}</Card.Meta>
                  <Card.Description>
                    <Icon name="crosshairs" />
                    {killStreak.kills}
                  </Card.Description>
                </Card.Content>
                <Card.Content extra>
                  <Card.Description>Meilleur meurtrier</Card.Description>
                </Card.Content>
              </Card>
            ) : null}
        </>
      );
    }

    render() {
      const { kills, killer } = this.state;

      console.log(killer);
      return (
        <div className="bodyDesktop">
          <Segment>
            <h2>Meurtres</h2>

            <>
              <Responsive as="div" {...Responsive.onlyComputer}>
                <Card.Group centered itemsPerRow={6} className="centered">
                  {this.Cards()}
                </Card.Group>
              </Responsive>
              <Responsive as="div" {...Responsive.onlyTablet}>
                <Card.Group centered itemsPerRow={4} className="centered">
                  {this.Cards()}
                </Card.Group>
              </Responsive>
              <Responsive as="div" {...Responsive.onlyMobile}>
                <Card.Group centered itemsPerRow={2} className="centered">
                  {this.Cards()}
                </Card.Group>
              </Responsive>
            </>
            <Table celled padded sortable>
              <Table.Header>
                <Table.Row>
                  <Table.HeaderCell>Victime</Table.HeaderCell>
                  <Table.HeaderCell>Meurtrier</Table.HeaderCell>
                  <Table.HeaderCell>Description</Table.HeaderCell>
                  <Table.HeaderCell>Date</Table.HeaderCell>
                  <Table.HeaderCell>Heure</Table.HeaderCell>
                </Table.Row>
              </Table.Header>
              <Table.Body>
                {kills.map((kill) => (
                  <Table.Row>
                    <Table.Cell textAlign="center">
                      <h5><a href={`/users/${kill.deathUser.id}`}>{kill.deathUser.pseudo}</a></h5>
                    </Table.Cell>
                    <Table.Cell textAlign="center">
                      <h5><a href={`/users/${kill.killerUser.id}`}>{kill.killerUser.pseudo}</a></h5>
                    </Table.Cell>
                    <Table.Cell>{kill.description}</Table.Cell>
                    <Table.Cell>{kill.jour}</Table.Cell>
                    <Table.Cell>{kill.heure}</Table.Cell>
                  </Table.Row>
                ))}
              </Table.Body>
            </Table>
          </Segment>
          <div className="spacer" />
        </div>
      );
    }
}
