import React, {Component} from 'react';
import PropTypes from 'prop-types';
import {
    Button, Icon, Divider, Form, Portal, Message, Table, Header,
} from 'semantic-ui-react';
import axios from 'axios';
import ImageUploader from '../utils/ImageUploader';
import config from '../../config.json';
import nameParser from '../../utils/nameParser';

class TeamGestion extends Component {
    constructor(props) {
        super(props);
        this.state = {
            showModal: false,
            faction: '',
            name: '',
            money: 0,
            score: 0,
            message: '',
            variant: 'negative',
            open: false,
            allItems: [],
            teamItems: [],
            allTeamSkills: [],
            showTeamSkills: false,
            teamSkills: [],
            users: []
        };
    }

    handleSubmit = (data) => {
        if ('picture' in data.object) {
            this.props.updatePic(data.object.picture);
            this.makeFeedback("positive", "Image mise a jour")
        }
        this.setState({showModal: false});
    }

    handleSubmitForm = () => {
        axios({
            method: 'put',
            url: `${config.back_Url}/teams/${this.props.team.id}`,
            headers: {Token: localStorage.getItem('killerToken')},
            data: {
                name: this.state.name,
                faction: this.state.faction,
                score: this.state.score,
                money: this.state.money,
            },
        }).then((data) => {
            if (data.status === 200) {
                const up = data.data;
                this.props.updateState(up.name, up.faction, up.money, up.score);
            }
        });
    }

    handleDelete = () => {
        axios({
            method: 'delete',
            url: `${config.back_Url}/teams/${this.props.team.id}`,
            headers: {Token: localStorage.getItem('killerToken')},
        }).then((data) => {
            if (data.data.status === 200) {
                localStorage.setItem('teamId', null);
                window.location.assign('/');
            } else {
                // erreur
            }
        });
    }

    componentDidMount = () => {
        this.setState({
            faction: this.props.team.faction,
            name: this.props.team.name,
            score: this.props.team.score,
            money: this.props.team.money,
            teamItems: this.props.team.teamItems,
            users: this.props.users,
            teamSkills: this.props.team.linkTeamSkills
        });
        this.getAllItems();
        this.getAllTeamSkills(this.props.users);
    }

    handleChange = (event) => {
        const {target} = event;
        const {value} = target;
        const {name} = target;

        this.setState({
            [name]: value,
        });
    }

    makeFeedback = (variant, message) => {
        this.setState({
            message,
            variant,
            open: true,
        });
        setTimeout(() => {
            this.setState({
                message: '',
                variant: 'positive',
                open: false,
            });
        }, 5000);
    }

    isAllowedToChangeFaction = () => {
        // tous les horraires sont UTC donc 18h UTC correspond à 20h en France
        const debutPeriode = 18;
        return (((new Date()).getUTCHours() >= debutPeriode) || (this.props.admin));
    }

    handleSubmitChangeTeam = () => {
        const {
            name, faction, money, score,
        } = this.state;
        this.makeFeedback('positive', name.toString() + ' ' + faction.toString(),)

        axios({
            method: 'put',
            url: `${config.back_Url}/teams/${this.props.team.id}`,
            headers: {Token: localStorage.getItem('killerToken')},
            data: {
                name: name,
                faction: faction,
                money: money,
                score: score,
            },
        }).then((data) => {
            if (data.data.status === 200) {
                const up = data.data.object;
                console.log(up)
                this.props.updateState(up);
            } else {
                this.makeFeedback(
                    'negative',
                    'erreur du cul',
                );
            }
        });
    }

    handleChangeRadio = (value) => {
        if (this.isAllowedToChangeFaction()) {
            this.setState({faction: value});
        } else {
            this.makeFeedback('negative', 'attends 20h pour pouvoir toucher a ça petit con');
        }
    }

    getAllItems = () => {
        axios({
            method: 'get',
            url: `${config.back_Url}/items/`,
            headers: {Token: localStorage.getItem('killerToken')},
        }).then((result) => {

            this.setState({allItems: result.data.object})
        });
    }

    buyItem = (item, price) => {
        axios.post(`${config.back_Url}/items/${this.props.team.id}`,
            {
                itemId: item.id,
                money: this.state.money,

            }, {
                headers: {Token: localStorage.getItem('killerToken')},
                data:{price: price,}
            }
        ).then(async r => {
            if (r.data.status !== 200) this.makeFeedback(
                'negative',
                r.data.message,
            );
            else {
                const itemBought = {
                    description: item.description,
                    name: item.name,
                    itemId: item.itemId,
                }

                // this.props.updateState({money: this.state.money - 25})

                this.setState({
                    money: this.state.money - price
                })

                this.props.updateState({
                    money: this.state.money - price
                })

                this.state.teamItems.push(itemBought);

                await this.getAllItems()

                this.makeFeedback(
                    'positive',
                    "objets mis a jour avec succés",
                );
            }
        })
        // .catch(e => {
        //         this.makeFeedback(
        //             'negative',
        //             e.message,
        //         );
        //     }
        // )
    }


    deleteItem = (item) => {
        axios.post(`${config.back_Url}/items/delete/${this.props.team.id}`,
            {
                itemId: item.itemId,
            }, {
                headers: {Token: localStorage.getItem('killerToken')}
            }
        ).then(async r => {

            const itemToRemove = this.state.teamItems.find((i) => i.itemId === item.itemId)

            this.setState({
                teamItems: this.state.teamItems.filter((i) => i !== itemToRemove),
            })

            await this.getAllItems()

            this.makeFeedback(
                'positive',
                r.data.message,
            );
        })
            .catch(e => {

                    this.makeFeedback(
                        'negative',
                        e.message,
                    );
                }
            )
    }

    getAllTeamSkills = (users) => {
        axios({
            method: 'get',
            url: `${config.back_Url}/skills/team`,
            headers: {Token: localStorage.getItem('killerToken')},
        }).then((result) => {
            axios({
                method: 'get',
                url: `${config.back_Url}/teams/${this.props.team.id}/users/`,
                headers: {Token: localStorage.getItem('killerToken')},
            }).then((data) => {
                result.data.object.map((s) => {
                    if (s.priceUserDepending === true) {
                        s.price = data.data.object.length * s.price
                    }
                    s.teamSkillId = s.id
                })
                this.setState({allTeamSkills: result.data.object})
            })
        });
    }

    isBuyable = (skill) => {
        const isBuyable = this.state.teamSkills.map((s) => {
                if (s.teamSkillId === skill.id) {
                    return false
                }
            }
        )
        return !isBuyable.includes(false);
    }

    buySkill = (skill) => {
        axios.post(`${config.back_Url}/skills/team/${this.props.team.id}`,
            {
                teamSkillId: skill.teamSkillId,
                price: skill.price,
                money: this.state.money
            }, {
                headers: {Token: localStorage.getItem('killerToken')}
            }
        ).then(async r => {
            if (r.data.status !== 200) this.makeFeedback(
                'negative',
                r.data.message,
            );
            else {
                const teamSkillBought = {
                    description: skill.description,
                    name: skill.name,
                    teamSkillId: skill.teamSkillId,
                    price: skill.price
                }

                this.props.updateState({money: this.state.money - skill.price})

                this.setState({
                    money: this.state.money - skill.price
                })

                this.state.teamSkills.push(teamSkillBought)

                await this.getAllTeamSkills()

                this.makeFeedback(
                    'positive',
                    "compétence mise a jour avec succés",
                );
            }
        }).catch(e => {
                this.makeFeedback(
                    'negative',
                    e.message,
                );
            }
        )
    }

    sellSkill = (skill) => {
        axios.post(`${config.back_Url}/skills/team/delete/${this.props.team.id}`,
            {
                teamSkillId: skill.teamSkillId,
                price: skill.price,
                money: this.state.money
            }, {
                headers: {Token: localStorage.getItem('killerToken')}
            }
        ).then(async r => {
            this.props.updateState({money: this.state.money + skill.price})

            const skillToRemove = this.state.teamSkills.find((s) => s.teamSkillId === skill.teamSkillId)

            this.setState({
                teamSkills: this.state.teamSkills.filter((s) => s !== skillToRemove),
                money: this.state.money + skill.price
            })

            await this.getAllTeamSkills()

            this.makeFeedback(
                'positive',
                r.data.message,
            );
        }).catch(e => {

                this.makeFeedback(
                    'negative',
                    e.message,
                );
            }
        )
    }


    render() {
        const {team} = this.props;
        const {
            faction, score, name, money, message, variant, open, allItems, teamItems, allTeamSkills, teamSkills,
        } = this.state;
        return (

            <div className="TeamGestion">
                <Portal open={open}>
                    <Message
                        style={{
                            left: '20px',
                            position: 'fixed',
                            bottom: '20px',
                            zIndex: 1000,
                        }}
                        negative={variant === 'negative'}
                        positive={variant === 'positive'}
                    >
                        <Message.Header>{variant === 'negative' ? 'Une erreur est survenue' : "Tout s'est déroulé comme prévu"}</Message.Header>
                        {message}
                    </Message>
                </Portal>
                {this.props.admin ? <><Divider/>
                    <h2>Compétences</h2>
                    <Table celled padded sortable>
                        <Table.Header>
                            <Table.Row>
                                <Table.HeaderCell>Nom</Table.HeaderCell>
                                <Table.HeaderCell>Description</Table.HeaderCell>
                                <Table.HeaderCell>Prix</Table.HeaderCell>
                                <Table.HeaderCell>Vendre</Table.HeaderCell>
                            </Table.Row>
                        </Table.Header>
                        <Table.Body>
                            {teamSkills.map((skill, key) => (
                                <Table.Row key={key}>
                                    <Table.Cell textAlign="center">
                                        <h5>{skill.name}</h5>
                                    </Table.Cell>
                                    <Table.Cell>{skill.description}</Table.Cell>
                                    <Table.Cell>{skill.price}</Table.Cell>
                                    <Table.Cell><Button style={{marginLeft: '2em'}}
                                                        onClick={() => this.sellSkill(skill)} negative>
                                        Vendre
                                    </Button></Table.Cell>

                                </Table.Row>
                            ))}
                        </Table.Body>
                    </Table>
                    <Header as="h2">Acheter une compétence</Header>
                    {this.state.showTeamSkills ? <Button onClick={() => {
                        this.setState({showTeamSkills: false});
                    }}>masquer les compétences restantes</Button> : <Button onClick={() => {
                        this.setState({showTeamSkills: true});
                    }}>voir les compétences restantes</Button>}
                    {this.state.showTeamSkills ? <div>
                        <Divider/>
                        <Table celled padded sortable>
                            <Table.Header>
                                <Table.Row>
                                    <Table.HeaderCell>Nom</Table.HeaderCell>
                                    <Table.HeaderCell>Description</Table.HeaderCell>
                                    <Table.HeaderCell>Prix</Table.HeaderCell>
                                    <Table.HeaderCell>Acheter</Table.HeaderCell>
                                </Table.Row>
                            </Table.Header>
                            <Table.Body>
                                {this.state.allTeamSkills.map((skill, key) => {
                                        if (this.isBuyable(skill) !== false) {
                                            return (<Table.Row key={key}>
                                                <Table.Cell textAlign="center">
                                                    <h5>{skill.name}</h5>
                                                </Table.Cell>
                                                <Table.Cell>{skill.description}</Table.Cell>
                                                <Table.Cell>{skill.price}</Table.Cell>
                                                <Table.Cell><Button style={{marginLeft: '2em'}}
                                                                    onClick={() => this.buySkill(skill)} positive>
                                                    Acheter
                                                </Button></Table.Cell>
                                            </Table.Row>)
                                        }
                                    }
                                )}
                            </Table.Body>
                        </Table>
                    </div> : <div/>}</> : <div/>}
                <Divider/>
                <h2>Gestion</h2>
                <Button size="small" type="button" onClick={() => this.setState({showModal: true})}>
                    <Icon name="photo"/>
                    Uploader une photo
                </Button>
                <Button size="small" color="red" type="button" onClick={this.handleDelete}>
                    <Icon name="trash"/>
                    Supprimer l&apos;équipe
                </Button>
                <ImageUploader uploadUrl={`/teams/${team.id}`} type="team" open={this.state.showModal}
                               handleSubmit={this.handleSubmit}/>
                {this.props.admin ? (

                    <div>

                    <Divider/>
                        <h2>Objets de l'équipe</h2>
                        <Table celled padded sortable>
                            <Table.Header>
                                <Table.Row>
                                    <Table.HeaderCell>Nom</Table.HeaderCell>
                                    <Table.HeaderCell>Description</Table.HeaderCell>
                                    <Table.HeaderCell>Utiliser</Table.HeaderCell>
                                </Table.Row>
                            </Table.Header>
                            <Table.Body>
                                {teamItems.map((item, key) => (
                                    <Table.Row key={key}>
                                        <Table.Cell textAlign="center">
                                            <h5>{item.name}</h5>
                                        </Table.Cell>
                                        <Table.Cell>{item.description}</Table.Cell>
                                        <Table.Cell>
                                            <Button style={{marginLeft: '2em'}}
                                                    onClick={() => this.deleteItem(item)} negative>
                                                Utiliser
                                            </Button>
                                        </Table.Cell>

                                    </Table.Row>
                                ))}
                            </Table.Body>
                        </Table>


                        <Header as="h2">Acheter un objet</Header>
                        <div>
                            <Divider/>
                            <Table celled padded sortable>
                                <Table.Header>
                                    <Table.Row>
                                        <Table.HeaderCell>Nom</Table.HeaderCell>
                                        <Table.HeaderCell>Description</Table.HeaderCell>
                                        <Table.HeaderCell>Acheter</Table.HeaderCell>
                                    </Table.Row>
                                </Table.Header>
                                <Table.Body>
                                    {allItems.map((item, key) => {
                                            return (<Table.Row key={key}>
                                                <Table.Cell textAlign="center">
                                                    <h5>{item.name}</h5>
                                                </Table.Cell>

                                                <Table.Cell>{item.description}</Table.Cell>
                                                <Table.Cell><Button style={{marginLeft: '2em'}}
                                                                    onClick={() => this.buyItem(item,0)} positive>
                                                    Acheter
                                                </Button></Table.Cell>
                                            </Table.Row>)

                                        }
                                    )}
                                </Table.Body>
                            </Table>
                        </div>


                        <Form onSubmit={this.handleSubmitForm} className="spacing">
                            <Form.Field>
                                <label>Nom</label>
                                <input value={name} name="name" onChange={this.handleChange}/>
                            </Form.Field>
                            <Form.Field>
                                <label>Argent</label>
                                <input value={money} name="money" onChange={this.handleChange}/>
                            </Form.Field>
                            <Form.Field>
                                <label>Score</label>
                                <input value={score} name="score" onChange={this.handleChange}/>
                            </Form.Field>
                            <Form.Group inline>
                                <label>Faction</label>
                                <Form.Radio
                                    label={nameParser('OUVRIER')}
                                    value="OUVRIER"
                                    checked={faction === 'OUVRIER'}
                                    onClick={() => this.handleChangeRadio('OUVRIER')}
                                />
                                <Form.Radio
                                    label={nameParser('NOBLE')}
                                    value="NOBLE"
                                    checked={faction === 'NOBLE'}
                                    onClick={() => this.handleChangeRadio('NOBLE')}
                                />
                                <Form.Radio
                                    label={nameParser('AMET')}
                                    value="AMET"
                                    checked={faction === 'AMET'}
                                    onClick={() => this.handleChangeRadio('AMET')}
                                />
                            </Form.Group>
                            <Button onClick={() => this.handleSubmitChangeTeam()}>Modifier l&apos;équipe</Button>
                        </Form>
                    </div>
                ) : (
                    <div>
                        <Form>
                            <Form.Group inline>
                                <label>Faction</label>
                                <Form.Radio
                                    label={nameParser('OUVRIER')}
                                    value="OUVRIER"
                                    checked={faction === 'OUVRIER'}
                                    onClick={() => this.handleChangeRadio('OUVRIER')}
                                />
                                <Form.Radio
                                    label={nameParser('NOBLE')}
                                    value="NOBLE"
                                    checked={faction === 'NOBLE'}
                                    onClick={() => this.handleChangeRadio('NOBLE')}
                                />
                                <Form.Radio
                                    label={nameParser('AMET')}
                                    value="AMET"
                                    checked={faction === 'AMET'}
                                    onClick={() => this.handleChangeRadio('AMET')}
                                />
                            </Form.Group>
                            <Button onClick={() => this.handleSubmitChangeTeam()}>Modifier l&apos;équipe</Button>
                        </Form>
                    </div>
                )
                }
            </div>
        );
    }
}


TeamGestion.propTypes = {
    admin: PropTypes.bool.isRequired,
    team: PropTypes.shape({
        id: PropTypes.number,
        picture: PropTypes.string,
        name: PropTypes.string,
        faction: PropTypes.oneOf(['NOBLE', 'OUVRIER', 'AMET']),
        score: PropTypes.number,
        money: PropTypes.number,
    }).isRequired,
    updatePic: PropTypes.func.isRequired,
    updateState: PropTypes.func.isRequired,
};

TeamGestion.defaultProp = {
    admin: false,

};

export default TeamGestion;
