import React, { Component } from 'react';
import axios from 'axios';
import underscore from 'underscore';
import { Segment, Comment } from 'semantic-ui-react';
import config from '../config.json';

import Message from '../components/message/message';

export default class Messages extends Component {
  constructor(props) {
    super(props);
    this.state = {
      messages: [],
    };
  }

  componentDidMount() {
    this.fetchData(0);
  }

  fetchData = (offset) => {
    axios({
      method: 'get',
      url: `${config.back_Url}/messages`,
      headers: { Token: localStorage.getItem('killerToken') },
      params: {
        offset,
      },
    }).then((data) => {
      if (data.data.status === 200) {
        if (data.data.object.length === 10) {
          this.setState((ps) => ({
            messages: [
              ...ps.messages,
              ...data.data.object,
            ],
          }));
          this.fetchData(offset + 10);
        } else {
          this.setState((ps) => ({
            messages: [
              ...ps.messages,
              ...data.data.object,
            ],
          }));
        }
      }
    });
  }

  render() {
    return (
      <div className="bodyDesktop">
        <div className="spacer" />
        <Segment>
          <h2>Messages</h2>
          <Comment.Group>
            {this.state.messages.length === 0
              ? "Il n'y a aucun message pour l'instant"
              : underscore.map(this.state.messages, (message) => (
                <Message
                  id={message.id}
                  titre={message.titre}
                  texte={message.texte}
                  faction={message.faction}
                />
              ))}
          </Comment.Group>
          <div className="spacer" />
        </Segment>
        <div className="spacer" />
      </div>
    );
  }
}
