import React, {useState} from 'react';
import {
    Form,
    Segment,
} from 'semantic-ui-react';

import confrontation from "../img/Confrontation.mp3"


export default function Covelba() {

    const [code, setCode] = useState("")
    return (
        <div className="bodyDesktop">
            <Segment style={{marginTop: '20px'}}>
                <Form>
                    <Form.Input value={code} onChange={(event) => setCode(event.target.value)}/>
                </Form>
                {code === "7717223131" ?
                    <div>
                        <p>
                            Alors que, victorieux, vous allez trouver Letus Covelba pour recevoir votre récompense, vous
                            le
                            surprenez en pleine conversation avec Akilon Sofos, l’ambassadeur Amet à Victoria…
                        </p>
                        <audio src={confrontation} controls/>
                    </div> : <></>}
            </Segment>
        </div>
    )
}
