import React, {Component} from 'react';
import {
    Segment, Header, Divider, Card, Button, Icon,
    Form, Table, Portal, Message,
} from 'semantic-ui-react';
import axios from 'axios';
import underscore from 'underscore';
import CardUser from '../../components/user/CardUser';
import config from '../../config.json';
import SearchUser from '../../components/user/SearchUser';

export default class teams extends Component {
    constructor(props) {
        super(props);

        const minute = (new Date()).getMinutes().toString();
        const hour = (new Date()).getHours().toString();
        const time = `${hour.length === 1 ? `0${hour}` : hour}:${minute.length === 1 ? `0${minute}` : minute}`;
        let day;
        console.log((new Date()).getDay)
        switch ((new Date()).getDay) {
            case 1:
                day = 'MARDI';
                break;

            case 2:
                day = 'MERCREDI';
                break;

            case 3:
                day = 'JEUDI';
                break;

            default:
                day = 'LUNDI';
        }
        this.state = {
            openKiller: false,
            openDeath: false,
            killer: null,
            death: null,
            kill: null,
            time,
            day,
            description: '',
            bonus: 0,
            message: null,
            variant: null,
            open: false,
            duel: false,
            descriptionError: false
        };
    }

    makeFeedback = (variant, message) => {
        this.setState({
            message,
            variant,
            open: true,
        });
        setTimeout(() => {
            this.setState({
                message: null,
                variant: null,
                open: false,
            });
        }, 5000);
    }

    handleChangeRadio = (e, {value}) => this.setState({day: value})

    handleDeleteUserKiller = () => {
        this.setState({
            killer: null,
            kill: null,
        });
    }

    handleDeleteUserDeath = () => {
        this.setState({
            death: null,
            kill: null,
        });
    }

    handleAddKiller = (data) => {
        if (data === null) {
            this.setState({
                openKiller: false,
            });
        } else {
            this.setState({
                openKiller: false,
                killer: data,
                kill: null,
            });
        }
    }

    handleAddDeath = (data) => {
        if (data === null) {
            this.setState({
                openDeath: false,
            });
        } else {
            this.setState({
                openDeath: false,
                death: data,
                kill: null,
            });
        }
    }

    handleCheckbox = (prop) => {
        this.setState((prevState) => ({
            [prop]: !prevState[prop],
            kill: null
        }));
    }

    handleChange = (event) => {
        const {target} = event;
        const {value} = target;
        const {name} = target;

        this.setState({descriptionError: false})

        this.setState({
            [name]: value,
            kill: null,
        });
    }

    validate = () => {
        const {
            killer, death, time, bonus, duel, description
        } = this.state;

        if (description.length > 500) this.setState({descriptionError: "La description est trop longue, 500 caracteres maximum"})

        if (this.state.descriptionError === false) axios({
            method: 'post',
            url: `${config.back_Url}/kills/validate/`,
            headers: {Token: localStorage.getItem('killerToken')},
            data: {
                killer: killer.id,
                death: death.id,
                heure: time,
                bonusScore: bonus,
                duel: duel
            },
        }).then((data) => {
            if (data.data.status === 200) {
                this.setState({
                    kill: data.data.object,
                });
            }
        });
    }

    kill = () => {
        const {
            killer, death, time, day, description, bonus, duel
        } = this.state;

        axios({
            method: 'post',
            url: `${config.back_Url}/kills/`,
            headers: {Token: localStorage.getItem('killerToken')},
            data: {
                killer: killer.id,
                death: death.id,
                heure: time,
                jour: day,
                description,
                bonusScore: bonus,
                duel: duel
            },
        }).then((data) => {
            if (data.data.status === 201) {
                const minute = (new Date()).getMinutes().toString();
                const hour = (new Date()).getHours().toString();
                const time2 = `${hour.length === 1 ? `0${hour}` : hour}:${minute.length === 1 ? `0${minute}` : minute}`;
                this.setState({
                    openKiller: false,
                    openDeath: false,
                    killer: null,
                    death: null,
                    kill: null,
                    bonus: 0,
                    time: time2,
                    day: 'LUNDI',
                    description: '',
                });
                this.makeFeedback(
                    'positive',
                    'Le meutre a été déclaré',
                );
            } else {
                this.makeFeedback(
                    'negative',
                    "Le meurtre n'a pas été déclaré",
                );
            }
        });
    }

    render() {
        const {
            killer, death, time, kill, day, description, bonus,
            message, variant, open, duel
        } = this.state;
        return (
            <div className="bodyDesktop">
                <div className="spacer"/>
                <Segment>
                    <Portal open={open}>
                        <Message
                            style={{
                                left: '20px',
                                position: 'fixed',
                                bottom: '20px',
                                zIndex: 1000,
                            }}
                            negative={variant === 'negative'}
                            positive={variant === 'positive'}
                        >
                            <Message.Header>{variant === 'negative' ? 'Une erreur est survenue' : "Tout s'est déroulé comme prévu"}</Message.Header>
                            {message}
                        </Message>
                    </Portal>
                    <h1> Déclarer un MEURTRE</h1>
                    <Divider/>
                    <Header as="h2">
                        <Icon name="crosshairs"/>
                        <Header.Content>Meutrier</Header.Content>
                    </Header>
                    <Card.Group doubling centered itemsPerRow={6} className="centered">
                        {killer === null ? <Card onClick={() => this.setState({openKiller: true})}><Card.Content
                                textAlign="center"><Icon size="big" color="black" fitted
                                                         name="add user"/></Card.Content></Card>
                            : (
                                <CardUser
                                    user={killer}
                                    admin
                                    handleDelete={(username) => this.handleDeleteUserKiller(username)}
                                />
                            )}
                    </Card.Group>
                    <SearchUser search="kill" open={this.state.openKiller} handleSubmit={this.handleAddKiller}/>
                    <Divider/>
                    <Header as="h2">
                        <Icon name="times"/>
                        <Header.Content>Victime</Header.Content>
                    </Header>
                    <Card.Group doubling centered itemsPerRow={6} className="centered">
                        {death === null ? <Card onClick={() => this.setState({openDeath: true})}><Card.Content
                                textAlign="center"><Icon size="big" color="black" fitted
                                                         name="add user"/></Card.Content></Card>
                            : (
                                <CardUser
                                    user={death}
                                    admin
                                    handleDelete={(username) => this.handleDeleteUserDeath(username)}
                                />
                            )}
                    </Card.Group>
                    <SearchUser
                        search="kill"
                        open={this.state.openDeath}
                        handleSubmit={this.handleAddDeath}
                    />
                    {killer === null ? null : <div>
                        <Divider/>
                        <Header as="h2">
                            <Icon name="industry"/>
                            <Header.Content>Compétences du killer</Header.Content>
                        </Header>
                        <Table celled padded sortable>
                            <Table.Header>
                                <Table.Row>
                                    <Table.HeaderCell>Nom</Table.HeaderCell>
                                    <Table.HeaderCell>Description</Table.HeaderCell>
                                </Table.Row>
                            </Table.Header>
                            <Table.Body>
                                {killer.userSkills.map((skill, key) => (
                                    <Table.Row key={key + "skill"}>
                                        <Table.Cell textAlign="center">
                                            <h5>{skill.name}</h5>
                                        </Table.Cell>
                                        <Table.Cell>{skill.description}</Table.Cell>
                                    </Table.Row>
                                ))}
                            </Table.Body>
                        </Table>
                    </div>}
                    <Divider/>
                    <Header as="h2">
                        <Icon name="settings"/>
                        <Header.Content>Paramètres</Header.Content>
                    </Header>
                    <div className="spacer"/>

                    <Form>
                        <Form.Group inline>
                            <label>Jour du meurtre</label>
                            <Form.Radio
                                label="Lundi"
                                value="LUNDI"
                                checked={day === 'LUNDI'}
                                onChange={this.handleChangeRadio}
                            />
                            <Form.Radio
                                label="Mardi"
                                value="MARDI"
                                checked={day === 'MARDI'}
                                onChange={this.handleChangeRadio}
                            />
                            <Form.Radio
                                label="Mercredi"
                                value="MERCREDI"
                                checked={day === 'MERCREDI'}
                                onChange={this.handleChangeRadio}
                            />
                            <Form.Radio
                                label="Jeudi"
                                value="JEUDI"
                                checked={day === 'JEUDI'}
                                onChange={this.handleChangeRadio}
                            />
                            <Form.Radio
                                label="Vendredi"
                                value="VENDREDI"
                                checked={day === 'VENDREDI'}
                                onChange={this.handleChangeRadio}
                            />
                        </Form.Group>
                        <Form.Group className="test">
                            <Form.Input type="time" name="time" label="Heure du meurtre" value={time}
                                        onChange={(e) => this.handleChange(e)}/>
                        </Form.Group>
                        <Form.Group widths="equal">
                            <Form.TextArea name="description" value={description} label="Description du meurtre"
                                           placeholder="On veut du sang..." onChange={(e) => this.handleChange(e)} error={this.state.descriptionError}/>
                        </Form.Group>
                        <Form.Checkbox label="Duel" name="duel" checked={duel}
                                       onChange={() => this.handleCheckbox('duel')}/>
                        <Form.Group className="test">
                            <Form.Input type="number" name="bonus" label="Points bonus" value={bonus}
                                        onChange={(e) => this.handleChange(e)}/>
                        </Form.Group>
                    </Form>

                    <div className="spacer"/>
                    <div className="spacer"/>
                    <Divider/>
                    <Header as="h2">
                        <Icon name="law"/>
                        <Header.Content>Validation</Header.Content>
                    </Header>

                    <div className="spacer"/>
                    <div className="spacer"/>
                    {kill !== null
                        ? (
                            <Table celled padded>
                                <Table.Header>
                                    <Table.Row>
                                        <Table.HeaderCell>Champs</Table.HeaderCell>
                                        <Table.HeaderCell>Valeur</Table.HeaderCell>
                                        <Table.HeaderCell>Commentaire</Table.HeaderCell>
                                        <Table.HeaderCell>Validation</Table.HeaderCell>
                                    </Table.Row>
                                </Table.Header>

                                <Table.Body>
                                    {underscore.map(kill.array, (row, key) => {
                                        let validation;
                                        if (row.validation === 1) {
                                            validation = <Icon name="thumbs up" color="green"/>;
                                        } else if (row.validation === -1) {
                                            validation = <Icon name="thumbs down" color="red"/>;
                                        } else {
                                            validation = <Icon name="hand paper" color="yellow"/>;
                                        }
                                        return (
                                            <Table.Row key={row.commentary + key}>
                                                <Table.Cell>{row.name}</Table.Cell>
                                                <Table.Cell>{row.value}</Table.Cell>
                                                <Table.Cell>{row.commentary}</Table.Cell>
                                                <Table.Cell textAlign="center">{validation}</Table.Cell>
                                            </Table.Row>
                                        );
                                    })}
                                </Table.Body>
                            </Table>
                        ) : null}

                    <Button icon labelPosition="left" onClick={this.validate}
                            disabled={killer === null || death === null || time === ''}>
                        <Icon name="check circle outline"/>
                        Vérifier
                    </Button>
                    <Button icon positive labelPosition="left" onClick={this.kill}
                            disabled={kill === null || killer === null || death === null || time === '' || kill.refuse}>
                        <Icon name="check circle"/>
                        Valider
                    </Button>
                </Segment>
                <div className="spacer"/>
            </div>
        );
    }
}
